/**
 * Audit Logging Service
 *
 * Implements security audit logging for authentication events, data exports, and account deletions
 * Constitutional requirement: Security First (V) - audit logging
 */

import { stmtCreateAuditLog } from '../database/db.js';

/**
 * Event types for audit logging
 */
export type AuditEventType =
  | 'auth_login'
  | 'auth_register'
  | 'auth_logout'
  | 'data_export'
  | 'account_deletion';

/**
 * Log authentication event (login, register, logout)
 *
 * @param userId - User ID who performed the action
 * @param eventType - Type of authentication event
 * @param ipAddress - IP address of the request
 * @param timestamp - Event timestamp (UTC milliseconds)
 * @param details - Optional additional context (JSON string)
 */
export function logAuthEvent(
  userId: number,
  eventType: 'auth_login' | 'auth_register' | 'auth_logout',
  ipAddress: string,
  timestamp: number,
  details?: string
): void {
  stmtCreateAuditLog.run(
    userId,
    eventType,
    ipAddress,
    timestamp,
    details ?? null
  );
}

/**
 * Log data export event
 *
 * @param userId - User ID who performed the export
 * @param exportType - Type of data exported (e.g., 'csv_workouts', 'csv_analytics', 'database')
 * @param timestamp - Event timestamp (UTC milliseconds)
 * @param ipAddress - Optional IP address of the request
 */
export function logDataExport(
  userId: number,
  exportType: string,
  timestamp: number,
  ipAddress?: string
): void {
  const details = JSON.stringify({ export_type: exportType });
  stmtCreateAuditLog.run(
    userId,
    'data_export',
    ipAddress ?? 'unknown',
    timestamp,
    details
  );
}

/**
 * Log account deletion event
 *
 * @param userId - User ID of the account being deleted
 * @param timestamp - Event timestamp (UTC milliseconds)
 * @param ipAddress - Optional IP address of the request
 * @param reason - Optional reason for deletion (e.g., 'user_initiated', 'admin_action')
 */
export function logAccountDeletion(
  userId: number,
  timestamp: number,
  ipAddress?: string,
  reason?: string
): void {
  const details = JSON.stringify({ reason: reason ?? 'user_initiated' });
  stmtCreateAuditLog.run(
    userId,
    'account_deletion',
    ipAddress ?? 'unknown',
    timestamp,
    details
  );
}

/**
 * Get audit logs for a specific user
 *
 * @param userId - User ID to retrieve logs for
 * @param limit - Maximum number of logs to retrieve (default: 100)
 * @returns Array of audit log entries
 */
export function getAuditLogsByUser(userId: number, limit: number = 100) {
  const stmt = stmtCreateAuditLog.database.prepare(`
    SELECT * FROM audit_logs
    WHERE user_id = ?
    ORDER BY timestamp DESC
    LIMIT ?
  `);

  return stmt.all(userId, limit);
}

/**
 * Get all audit logs within a date range
 *
 * @param startTimestamp - Start of date range (UTC milliseconds)
 * @param endTimestamp - End of date range (UTC milliseconds)
 * @param limit - Maximum number of logs to retrieve (default: 100)
 * @returns Array of audit log entries
 */
export function getAuditLogsByDateRange(
  startTimestamp: number,
  endTimestamp: number,
  limit: number = 100
) {
  const stmt = stmtCreateAuditLog.database.prepare(`
    SELECT * FROM audit_logs
    WHERE timestamp >= ? AND timestamp <= ?
    ORDER BY timestamp DESC
    LIMIT ?
  `);

  return stmt.all(startTimestamp, endTimestamp, limit);
}

/**
 * Workout Service
 *
 * Business logic for workout session management:
 * - Creating new workout sessions
 * - Listing workouts with optional date filtering
 * - Updating workout status and metrics
 */

import {
  stmtCreateWorkout,
  stmtGetWorkoutsByUser,
  stmtGetWorkoutsByUserDateRange,
  stmtUpdateWorkoutStatus,
  db,
} from '../database/db.js';

/**
 * Workout interface matching database schema + program day info
 */
export interface Workout {
  id: number;
  user_id: number;
  program_day_id: number;
  date: string; // ISO format: YYYY-MM-DD
  started_at: number | null;
  completed_at: number | null;
  status: 'not_started' | 'in_progress' | 'completed' | 'cancelled';
  total_volume_kg: number | null;
  average_rir: number | null;
  synced: number;
  day_name: string | null; // Program day name (e.g., "Push A (Chest-Focused)")
  day_type: 'strength' | 'vo2max' | null; // Program day type
}

/**
 * Create a new workout session
 *
 * @param userId - ID of the user creating the workout
 * @param programDayId - ID of the program day (e.g., Push A, Pull A)
 * @param date - Date in ISO format (YYYY-MM-DD)
 * @returns The created workout object
 */
export function createWorkout(
  userId: number,
  programDayId: number,
  date: string
): Workout {
  // Validate date format (basic check)
  if (!/^\d{4}-\d{2}-\d{2}$/.test(date)) {
    throw new Error('Invalid date format. Expected YYYY-MM-DD');
  }

  // Insert workout with default status=not_started
  const result = stmtCreateWorkout.run(userId, programDayId, date);
  const workoutId = result.lastInsertRowid as number;

  // Return the created workout
  const workout = db
    .prepare('SELECT * FROM workouts WHERE id = ?')
    .get(workoutId) as Workout;

  return workout;
}

/**
 * Get exercises for a program day
 *
 * @param programDayId - Program day ID
 * @returns Array of exercises with sets/reps
 */
function getProgramExercises(programDayId: number) {
  return db
    .prepare(
      `SELECT pe.*, e.name as exercise_name
       FROM program_exercises pe
       JOIN exercises e ON pe.exercise_id = e.id
       WHERE pe.program_day_id = ?
       ORDER BY pe.order_index ASC`
    )
    .all(programDayId);
}

/**
 * List workouts for a user with optional date filtering
 * Includes program exercises for each workout
 *
 * @param userId - ID of the user
 * @param startDate - Optional start date filter (YYYY-MM-DD)
 * @param endDate - Optional end date filter (YYYY-MM-DD)
 * @returns Array of workout objects with exercises
 */
export function listWorkouts(
  userId: number,
  startDate?: string,
  endDate?: string
): any[] {
  let workouts: Workout[];

  // If both date filters provided, use date range query
  if (startDate && endDate) {
    workouts = stmtGetWorkoutsByUserDateRange.all(
      userId,
      startDate,
      endDate
    ) as Workout[];
  } else if (startDate) {
    // If only start date, filter in application layer
    const allWorkouts = stmtGetWorkoutsByUser.all(userId) as Workout[];
    workouts = allWorkouts.filter((w) => w.date >= startDate);
  } else if (endDate) {
    // If only end date, filter in application layer
    const allWorkouts = stmtGetWorkoutsByUser.all(userId) as Workout[];
    workouts = allWorkouts.filter((w) => w.date <= endDate);
  } else {
    // No filters, return all workouts
    workouts = stmtGetWorkoutsByUser.all(userId) as Workout[];
  }

  // Attach exercises to each workout
  return workouts.map((workout) => ({
    ...workout,
    exercises: getProgramExercises(workout.program_day_id),
  }));
}

/**
 * Update workout status and metrics
 *
 * @param workoutId - ID of the workout to update
 * @param status - New status (in_progress, completed, cancelled)
 * @param totalVolumeKg - Optional total volume in kg (sets Ã— reps Ã— weight)
 * @param averageRir - Optional average RIR across all sets
 * @returns The updated workout object
 */
export function updateWorkoutStatus(
  workoutId: number,
  status: 'not_started' | 'in_progress' | 'completed' | 'cancelled',
  totalVolumeKg?: number,
  averageRir?: number
): Workout {
  // Calculate completed_at timestamp if status is completed
  const completedAt = status === 'completed' ? Date.now() : null;

  // Update workout
  stmtUpdateWorkoutStatus.run(
    status,
    completedAt,
    totalVolumeKg ?? null,
    averageRir ?? null,
    workoutId
  );

  // Return updated workout
  const workout = db
    .prepare('SELECT * FROM workouts WHERE id = ?')
    .get(workoutId) as Workout;

  return workout;
}

/**
 * Recovery Assessment Service
 *
 * Business logic for daily recovery check-in and auto-regulation:
 * - 3-question assessment (sleep, soreness, motivation) on 1-5 scale
 * - Total score calculation (3-15 range)
 * - Volume adjustment logic per FR-009
 */

import { stmtCreateRecoveryAssessment, calculateVolumeAdjustment } from '../database/db.js';

/**
 * Recovery assessment response interface
 */
export interface RecoveryAssessmentResponse {
  total_score: number;
  volume_adjustment: 'none' | 'reduce_1_set' | 'reduce_2_sets' | 'rest_day';
}

/**
 * Create a new recovery assessment
 *
 * Calculates total recovery score from 3 subscores (sleep quality,
 * muscle soreness, mental motivation) and determines volume adjustment
 * based on auto-regulation logic per FR-008 and FR-009.
 *
 * Auto-Regulation Logic (FR-009):
 * - Score 12-15: No adjustment (good recovery)
 * - Score 9-11: Reduce by 1 set per exercise
 * - Score 6-8: Reduce by 2 sets per exercise
 * - Score 3-5: Rest day recommended
 *
 * @param userId - ID of the user submitting assessment
 * @param date - Assessment date in ISO format (YYYY-MM-DD)
 * @param sleepQuality - Sleep quality rating (1-5 scale)
 * @param muscleSoreness - Muscle soreness rating (1-5 scale, inverted)
 * @param mentalMotivation - Mental motivation rating (1-5 scale)
 * @returns RecoveryAssessmentResponse with total_score and volume_adjustment
 */
export function createAssessment(
  userId: number,
  date: string,
  sleepQuality: number,
  muscleSoreness: number,
  mentalMotivation: number
): RecoveryAssessmentResponse {
  // Validate input ranges (1-5 per FR-008)
  if (sleepQuality < 1 || sleepQuality > 5) {
    throw new Error('Sleep quality must be between 1 and 5');
  }
  if (muscleSoreness < 1 || muscleSoreness > 5) {
    throw new Error('Muscle soreness must be between 1 and 5');
  }
  if (mentalMotivation < 1 || mentalMotivation > 5) {
    throw new Error('Mental motivation must be between 1 and 5');
  }

  // Validate date format (ISO YYYY-MM-DD)
  const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
  if (!dateRegex.test(date)) {
    throw new Error('Date must be in ISO format (YYYY-MM-DD)');
  }

  // Calculate total score (sum of 3 subscores, range 3-15)
  const totalScore = sleepQuality + muscleSoreness + mentalMotivation;

  // Determine volume adjustment based on total score (FR-009)
  const volumeAdjustment = calculateVolumeAdjustment(totalScore);

  // Insert recovery assessment into database
  const timestamp = Date.now();
  stmtCreateRecoveryAssessment.run(
    userId,
    date,
    sleepQuality,
    muscleSoreness,
    mentalMotivation,
    totalScore,
    volumeAdjustment,
    timestamp
  );

  // Log assessment for monitoring
  console.log(
    `Recovery assessment created: user=${userId}, date=${date}, ` +
      `score=${totalScore} (sleep=${sleepQuality}, soreness=${muscleSoreness}, ` +
      `motivation=${mentalMotivation}), adjustment=${volumeAdjustment}`
  );

  return {
    total_score: totalScore,
    volume_adjustment: volumeAdjustment,
  };
}

/**
 * Analytics Service (T047-T049)
 *
 * Provides analytics data for 1RM progression, volume trends, and consistency metrics.
 * Uses prepared statements from db.ts for optimal performance.
 */

import {
  stmt1RMProgression,
  stmtVolumeTrends,
  stmtConsistencyMetrics,
} from '../database/db.js';

/**
 * Volume landmarks for muscle groups (from mobile constants)
 */
const VOLUME_LANDMARKS: Record<string, { mev: number; mav: number; mrv: number }> = {
  chest: { mev: 8, mav: 14, mrv: 22 },
  back_lats: { mev: 10, mav: 16, mrv: 26 },
  back_traps: { mev: 6, mav: 12, mrv: 20 },
  shoulders_front: { mev: 4, mav: 8, mrv: 14 },
  shoulders_side: { mev: 8, mav: 16, mrv: 26 },
  shoulders_rear: { mev: 8, mav: 14, mrv: 22 },
  biceps: { mev: 6, mav: 12, mrv: 20 },
  triceps: { mev: 6, mav: 12, mrv: 22 },
  quads: { mev: 8, mav: 14, mrv: 24 },
  hamstrings: { mev: 6, mav: 12, mrv: 20 },
  glutes: { mev: 6, mav: 12, mrv: 20 },
  calves: { mev: 8, mav: 14, mrv: 22 },
  abs: { mev: 8, mav: 16, mrv: 28 },
};

/**
 * 1RM Progression Data Point
 */
export interface OneRMProgressionPoint {
  date: string;
  estimated_1rm: number;
}

/**
 * Volume Trends Data Point
 */
export interface VolumeTrendsPoint {
  week: string;
  total_sets: number;
  mev: number;
  mav: number;
  mrv: number;
}

/**
 * Consistency Metrics
 */
export interface ConsistencyMetrics {
  adherence_rate: number;
  avg_session_duration: number;
  total_workouts: number;
}

/**
 * Get 1RM progression for a specific exercise over time (T047)
 *
 * Calculates estimated 1RM using Epley formula with RIR adjustment:
 * 1RM = weight * (1 + (reps - rir) / 30)
 *
 * @param userId User ID
 * @param exerciseId Exercise ID
 * @param startDate Start date (YYYY-MM-DD)
 * @param endDate End date (YYYY-MM-DD)
 * @returns Array of {date, estimated_1rm} sorted by date
 */
export function get1RMProgression(
  userId: number,
  exerciseId: number,
  startDate: string,
  endDate: string
): OneRMProgressionPoint[] {
  const results = stmt1RMProgression.all(userId, exerciseId, startDate, endDate) as Array<{
    date: string;
    estimated_1rm: number;
  }>;

  return results.map((row) => ({
    date: row.date,
    estimated_1rm: Math.round(row.estimated_1rm * 10) / 10, // Round to 1 decimal place
  }));
}

/**
 * Get volume trends for a muscle group over time (T048)
 *
 * Groups sets by ISO week and includes MEV/MAV/MRV landmarks.
 *
 * @param userId User ID
 * @param muscleGroup Muscle group name (e.g., 'chest', 'back_lats')
 * @param startDate Start date (YYYY-MM-DD)
 * @param endDate End date (YYYY-MM-DD)
 * @returns Array of {week, total_sets, mev, mav, mrv} sorted by week
 */
export function getVolumeTrends(
  userId: number,
  muscleGroup: string,
  startDate: string,
  endDate: string
): VolumeTrendsPoint[] {
  const results = stmtVolumeTrends.all(userId, startDate, endDate, muscleGroup) as Array<{
    week: string;
    total_sets: number;
  }>;

  // Get volume landmarks for this muscle group
  const landmarks = VOLUME_LANDMARKS[muscleGroup] || { mev: 0, mav: 0, mrv: 0 };

  return results.map((row) => ({
    week: row.week,
    total_sets: row.total_sets,
    mev: landmarks.mev,
    mav: landmarks.mav,
    mrv: landmarks.mrv,
  }));
}

/**
 * Get consistency metrics for a user (T049)
 *
 * Calculates:
 * - adherence_rate: completed_workouts / total_workouts (scheduled)
 * - avg_session_duration: mean duration in seconds
 * - total_workouts: count of all workouts
 *
 * @param userId User ID
 * @returns Consistency metrics object
 */
export function getConsistencyMetrics(userId: number): ConsistencyMetrics {
  const result = stmtConsistencyMetrics.get(userId) as {
    completed_workouts: number;
    total_workouts: number;
    avg_session_duration: number | null;
  };

  // Calculate adherence rate (avoid division by zero)
  const adherenceRate =
    result.total_workouts > 0 ? result.completed_workouts / result.total_workouts : 0;

  return {
    adherence_rate: Math.round(adherenceRate * 1000) / 1000, // Round to 3 decimal places
    avg_session_duration: result.avg_session_duration || 0,
    total_workouts: result.total_workouts,
  };
}

/**
 * Set Logging Service
 *
 * Business logic for exercise set tracking:
 * - Logging individual sets with weight, reps, RIR
 * - Deduplication via localId for idempotent sync
 * - 1RM calculation using Epley formula with RIR adjustment
 */

import { stmtLogSet, db, calculateOneRepMax } from '../database/db.js';

/**
 * Set interface matching database schema
 */
export interface Set {
  id: number;
  workout_id: number;
  exercise_id: number;
  set_number: number;
  weight_kg: number;
  reps: number;
  rir: number;
  timestamp: number;
  notes: string | null;
  synced: number;
}

/**
 * Set logging response (for sync confirmation)
 */
export interface LogSetResponse {
  id: number;
  localId: number | null;
  synced: boolean;
  estimated_1rm: number;
  weight_kg: number;
  reps: number;
  rir: number;
}

/**
 * Log a new exercise set
 *
 * Implements idempotent sync: if localId exists for this workout,
 * returns existing set instead of creating duplicate.
 *
 * @param workoutId - ID of the workout session
 * @param exerciseId - ID of the exercise being performed
 * @param setNumber - Set number within the workout (1, 2, 3, etc.)
 * @param weightKg - Weight lifted in kilograms (0-500)
 * @param reps - Number of repetitions completed (1-50)
 * @param rir - Reps in Reserve (0-4)
 * @param timestamp - UTC milliseconds when set was completed
 * @param localId - Optional local ID from mobile app for deduplication
 * @param notes - Optional notes about the set (max 500 chars)
 * @returns LogSetResponse with id, localId, and synced status
 */
export function logSet(
  workoutId: number,
  exerciseId: number,
  setNumber: number,
  weightKg: number,
  reps: number,
  rir: number,
  timestamp: number,
  localId?: number,
  notes?: string
): LogSetResponse {
  // Validate input ranges (per FR-005)
  if (weightKg < 0 || weightKg > 500) {
    throw new Error('Weight must be between 0 and 500 kg');
  }
  if (reps < 1 || reps > 50) {
    throw new Error('Reps must be between 1 and 50');
  }
  if (rir < 0 || rir > 4) {
    throw new Error('RIR must be between 0 and 4');
  }

  // Validate notes length
  if (notes && notes.length > 500) {
    throw new Error('Notes must be 500 characters or less');
  }

  // Deduplication check: if localId provided, check if set already exists
  // This enables idempotent sync from mobile app
  if (localId) {
    const existingSet = db
      .prepare(
        `SELECT id, weight_kg, reps, rir FROM sets
         WHERE workout_id = ? AND id = ?`
      )
      .get(workoutId, localId) as { id: number; weight_kg: number; reps: number; rir: number } | undefined;

    if (existingSet) {
      // Set already exists, return existing ID with calculated 1RM
      const estimated1RM = calculateOneRepMax(existingSet.weight_kg, existingSet.reps, existingSet.rir);
      return {
        id: existingSet.id,
        localId: localId,
        synced: true,
        estimated_1rm: estimated1RM,
        weight_kg: existingSet.weight_kg,
        reps: existingSet.reps,
        rir: existingSet.rir,
      };
    }
  }

  // Insert new set with synced=1 (server is source of truth)
  const result = stmtLogSet.run(
    workoutId,
    exerciseId,
    setNumber,
    weightKg,
    reps,
    rir,
    timestamp,
    notes ?? null
  );

  const setId = result.lastInsertRowid as number;

  // Calculate estimated 1RM for analytics (Epley formula with RIR)
  // Formula: 1RM = weight Ã— (1 + (reps - rir) / 30)
  const estimated1RM = calculateOneRepMax(weightKg, reps, rir);

  // Log for performance monitoring (should be < 5ms per CLAUDE.md)
  console.log(
    `Set logged: workout=${workoutId}, exercise=${exerciseId}, ` +
      `${weightKg}kg Ã— ${reps} @ RIR ${rir} (Est. 1RM: ${estimated1RM.toFixed(1)}kg)`
  );

  return {
    id: setId,
    localId: localId ?? null,
    synced: true,
    estimated_1rm: estimated1RM,
    weight_kg: weightKg,
    reps: reps,
    rir: rir,
  };
}

/**
 * Get all sets for a workout
 *
 * @param workoutId - ID of the workout session
 * @returns Array of sets
 */
export function getSetsForWorkout(workoutId: number): Set[] {
  const sets = db
    .prepare(
      `SELECT id, workout_id, exercise_id, set_number, weight_kg, reps, rir, timestamp, notes, synced
       FROM sets
       WHERE workout_id = ?
       ORDER BY id ASC`
    )
    .all(workoutId) as Set[];

  return sets;
}
