/**
 * FitFlow Pro Backend Server
 *
 * Fastify API server with JWT authentication, SQLite database, and REST endpoints
 */

import Fastify from 'fastify';
import cors from '@fastify/cors';
import jwt from '@fastify/jwt';
import authRoutes from './routes/auth.js';
import workoutRoutes from './routes/workouts.js';
import setRoutes from './routes/sets.js';
import recoveryRoutes from './routes/recovery.js';
import analyticsRoutes from './routes/analytics.js';
import programDaysRoutes from './routes/programDays.js';
import exerciseRoutes from './routes/exercises.js';
import programRoutes from './routes/programs.js';
import programExerciseRoutes from './routes/program-exercises.js';
import vo2maxRoutes from './routes/vo2max.js';

const JWT_SECRET = process.env.JWT_SECRET || 'fitflow-dev-secret-change-in-production';
const PORT = parseInt(process.env.PORT || '3000', 10);
const HOST = process.env.HOST || '0.0.0.0';

/**
 * Build Fastify application instance
 */
export async function buildApp() {
  const app = Fastify({
    logger: {
      level: process.env.LOG_LEVEL || 'info',
    },
    trustProxy: true, // Required for Nginx reverse proxy (X-Forwarded-* headers)
  });

  // Register CORS
  await app.register(cors, {
    origin: true, // Allow all origins in development
    credentials: true,
  });

  // Register JWT plugin
  await app.register(jwt, {
    secret: JWT_SECRET,
    sign: {
      expiresIn: '30d', // 30-day expiration (justified constitutional violation for home server)
    },
  });

  // Health check endpoint
  app.get('/health', async () => {
    return { status: 'ok', timestamp: Date.now() };
  });

  // Register routes
  await app.register(authRoutes, { prefix: '/api' });
  await app.register(workoutRoutes, { prefix: '/api' });
  await app.register(setRoutes, { prefix: '/api' });
  await app.register(recoveryRoutes, { prefix: '/api' });
  await app.register(analyticsRoutes, { prefix: '/api' });
  await app.register(programDaysRoutes, { prefix: '/api' });
  await app.register(exerciseRoutes, { prefix: '/api/exercises' });
  await app.register(programRoutes, { prefix: '/api' });
  await app.register(programExerciseRoutes, { prefix: '/api' });
  await app.register(vo2maxRoutes, { prefix: '/api' });

  return app;
}

/**
 * Start server (only if running directly, not in tests)
 */
async function start() {
  try {
    const app = await buildApp();
    await app.listen({ port: PORT, host: HOST });
    console.log(`FitFlow Pro API server listening on ${HOST}:${PORT}`);
  } catch (err) {
    console.error('Failed to start server:', err);
    process.exit(1);
  }
}

// Only start if this is the main module
if (import.meta.url === `file://${process.argv[1]}`) {
  start();
}

export default buildApp;

/**
 * Authentication Routes
 *
 * Endpoints for user registration, login, and account deletion
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { registerUser, loginUser } from '../services/authService.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';
import { stmtDeleteUser } from '../database/db.js';
import { logAuthEvent, logAccountDeletion } from '../services/auditService.js';

/**
 * Registration request schema
 */
const registerSchema = {
  schema: {
    body: {
      type: 'object',
      required: ['username', 'password'],
      properties: {
        username: {
          type: 'string',
          format: 'email',
          description: 'User email address (used as username)',
        },
        password: {
          type: 'string',
          minLength: 8,
          description: 'Password (minimum 8 characters)',
        },
        age: {
          type: 'integer',
          minimum: 13,
          maximum: 100,
          description: 'User age (13-100 years)',
        },
        weight_kg: {
          type: 'number',
          minimum: 30,
          maximum: 300,
          description: 'User weight in kg (30-300)',
        },
        experience_level: {
          type: 'string',
          enum: ['beginner', 'intermediate', 'advanced'],
          description: 'Training experience level',
        },
      },
    },
    response: {
      201: {
        type: 'object',
        properties: {
          user_id: { type: 'number' },
          userId: { type: 'number' },
          username: { type: 'string' },
          token: { type: 'string' },
        },
      },
    },
  },
};

/**
 * Login request schema
 */
const loginSchema = {
  schema: {
    body: {
      type: 'object',
      required: ['username', 'password'],
      properties: {
        username: {
          type: 'string',
          description: 'User email address',
        },
        password: {
          type: 'string',
          description: 'User password',
        },
      },
    },
    response: {
      200: {
        type: 'object',
        properties: {
          token: { type: 'string' },
          user: {
            type: 'object',
            properties: {
              id: { type: 'number' },
              username: { type: 'string' },
              age: { type: 'number' },
              weight_kg: { type: 'number' },
              experience_level: { type: 'string' },
              created_at: { type: 'number' },
              updated_at: { type: 'number' },
            },
          },
        },
      },
    },
  },
};

/**
 * Registration request body interface
 */
interface RegisterBody {
  username: string;
  password: string;
  age?: number;
  weight_kg?: number;
  experience_level?: 'beginner' | 'intermediate' | 'advanced';
}

/**
 * Login request body interface
 */
interface LoginBody {
  username: string;
  password: string;
}

/**
 * Delete user route params interface
 */
interface DeleteUserParams {
  id: string;
}

/**
 * Register authentication routes
 */
export default async function authRoutes(fastify: FastifyInstance) {
  /**
   * POST /api/auth/register
   *
   * Register a new user with email (username), password, and optional profile data
   * Returns user_id and JWT token on success
   */
  fastify.post<{ Body: RegisterBody }>(
    '/auth/register',
    registerSchema,
    async (request: FastifyRequest<{ Body: RegisterBody }>, reply: FastifyReply) => {
      try {
        const { username, password, age, weight_kg, experience_level } = request.body;

        // Call authentication service
        const result = await registerUser(
          username,
          password,
          age,
          weight_kg,
          experience_level,
          (payload) => fastify.jwt.sign(payload)
        );

        // Log successful registration
        const ipAddress = request.ip || 'unknown';
        const timestamp = Date.now();
        logAuthEvent(result.user_id, 'auth_register', ipAddress, timestamp);

        return reply.status(201).send(result);
      } catch (error) {
        // Handle duplicate username error
        if (error instanceof Error && error.message === 'Username already exists') {
          return reply.status(409).send({
            error: 'Username already exists',
          });
        }

        // Generic error
        fastify.log.error(error);
        return reply.status(400).send({
          error: 'Registration failed',
        });
      }
    }
  );

  /**
   * POST /api/auth/login
   *
   * Login with username (email) and password
   * Returns JWT token and user data on success
   */
  fastify.post<{ Body: LoginBody }>(
    '/auth/login',
    loginSchema,
    async (request: FastifyRequest<{ Body: LoginBody }>, reply: FastifyReply) => {
      try {
        const { username, password } = request.body;

        // Call authentication service
        const result = await loginUser(
          username,
          password,
          (payload) => fastify.jwt.sign(payload)
        );

        // Log successful login
        const ipAddress = request.ip || 'unknown';
        const timestamp = Date.now();
        logAuthEvent(result.user.id, 'auth_login', ipAddress, timestamp);

        return reply.status(200).send(result);
      } catch (error) {
        // Handle invalid credentials error
        if (error instanceof Error && error.message === 'Invalid credentials') {
          return reply.status(401).send({
            error: 'Invalid credentials',
          });
        }

        // Generic error
        fastify.log.error(error);
        return reply.status(401).send({
          error: 'Login failed',
        });
      }
    }
  );

  /**
   * DELETE /api/users/:id
   *
   * Delete user account (requires JWT authentication)
   * User can only delete their own account
   * Cascade deletes all related data: workouts, sets, recovery assessments, programs
   * Returns 204 No Content on success
   */
  fastify.delete<{ Params: DeleteUserParams }>(
    '/users/:id',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest<{ Params: DeleteUserParams }>, reply: FastifyReply) => {
      try {
        const userId = parseInt(request.params.id, 10);
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Validate user ID
        if (isNaN(userId)) {
          return reply.status(400).send({
            error: 'Invalid user ID',
          });
        }

        // Users can only delete their own account
        if (authenticatedUser.userId !== userId) {
          return reply.status(403).send({
            error: 'Forbidden - You can only delete your own account',
          });
        }

        // Log deletion to audit log before deleting user
        const auditTimestamp = Date.now();
        const ipAddress = request.ip || 'unknown';
        logAccountDeletion(userId, auditTimestamp, ipAddress, 'user_initiated');

        // Delete user (cascade delete handled by database foreign keys)
        // ON DELETE CASCADE will remove:
        // - workouts  sets (via workout_id FK)
        // - recovery_assessments
        // - vo2max_sessions (via workout_id FK)
        // - programs
        // - active_sessions
        // - audit_logs
        const result = stmtDeleteUser.run(userId);

        if (result.changes === 0) {
          return reply.status(404).send({
            error: 'User not found',
          });
        }

        // Return 204 No Content on success (no body)
        return reply.status(204).send();
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to delete user account',
        });
      }
    }
  );
}

/**
 * Workout Routes
 *
 * REST endpoints for workout session management:
 * - POST /api/workouts - Create new workout session
 * - GET /api/workouts - List user's workouts with optional date filtering
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { createWorkout, listWorkouts, updateWorkoutStatus } from '../services/workoutService.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';
import { stmtGetWorkoutById, db } from '../database/db.js';

/**
 * Create workout request body interface
 */
interface CreateWorkoutBody {
  program_day_id: number;
  date: string;
}

/**
 * List workouts query parameters interface
 */
interface ListWorkoutsQuery {
  start_date?: string;
  end_date?: string;
  workout_id?: number;
}

/**
 * Create workout request schema
 */
const createWorkoutSchema = {
  schema: {
    body: {
      type: 'object',
      required: ['program_day_id', 'date'],
      properties: {
        program_day_id: {
          type: 'integer',
          description: 'ID of the program day (e.g., Push A, Pull A)',
        },
        date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
          description: 'Date in ISO format (YYYY-MM-DD)',
        },
      },
    },
    response: {
      201: {
        type: 'object',
        properties: {
          id: { type: 'number' },
          user_id: { type: 'number' },
          program_day_id: { type: 'number' },
          date: { type: 'string' },
          started_at: { type: ['number', 'null'] },
          completed_at: { type: ['number', 'null'] },
          status: {
            type: 'string',
            enum: ['not_started', 'in_progress', 'completed', 'cancelled'],
          },
          total_volume_kg: { type: ['number', 'null'] },
          average_rir: { type: ['number', 'null'] },
          synced: { type: 'number' },
        },
      },
    },
  },
};

/**
 * List workouts request schema
 */
const listWorkoutsSchema = {
  schema: {
    querystring: {
      type: 'object',
      properties: {
        start_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
          description: 'Start date filter (YYYY-MM-DD)',
        },
        end_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
          description: 'End date filter (YYYY-MM-DD)',
        },
        workout_id: {
          type: 'integer',
          description: 'Fetch specific workout by ID',
        },
      },
    },
    response: {
      200: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            id: { type: 'number' },
            user_id: { type: 'number' },
            program_day_id: { type: 'number' },
            date: { type: 'string' },
            started_at: { type: ['number', 'null'] },
            completed_at: { type: ['number', 'null'] },
            status: {
              type: 'string',
              enum: ['not_started', 'in_progress', 'completed', 'cancelled'],
            },
            total_volume_kg: { type: ['number', 'null'] },
            average_rir: { type: ['number', 'null'] },
            synced: { type: 'number' },
            day_name: { type: ['string', 'null'] },
            day_type: {
              type: ['string', 'null'],
              enum: ['strength', 'vo2max', null],
            },
            exercises: {
              type: 'array',
              items: {
                type: 'object',
                properties: {
                  id: { type: 'number' },
                  program_day_id: { type: 'number' },
                  exercise_id: { type: 'number' },
                  exercise_name: { type: 'string' },
                  order_index: { type: 'number' },
                  sets: { type: 'number' },
                  reps: { type: 'string' },
                  rir: { type: 'number' },
                },
              },
            },
          },
        },
      },
    },
  },
};

/**
 * Register workout routes
 */
export default async function workoutRoutes(fastify: FastifyInstance) {
  /**
   * POST /api/workouts
   *
   * Create a new workout session for the authenticated user
   * Returns the created workout object with status=not_started
   *
   * Requires JWT authentication
   */
  fastify.post<{ Body: CreateWorkoutBody }>(
    '/workouts',
    {
      ...createWorkoutSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Body: CreateWorkoutBody }>,
      reply: FastifyReply
    ) => {
      try {
        const { program_day_id, date } = request.body;
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Create workout for authenticated user
        const workout = createWorkout(
          authenticatedUser.userId,
          program_day_id,
          date
        );

        return reply.status(201).send(workout);
      } catch (error) {
        // Handle validation errors
        if (error instanceof Error && error.message.includes('Invalid date')) {
          return reply.status(400).send({
            error: error.message,
          });
        }

        // Generic error
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to create workout',
        });
      }
    }
  );

  /**
   * GET /api/workouts
   *
   * List workouts for the authenticated user
   * Optional query parameters for date filtering:
   * - start_date: Return workouts on or after this date (YYYY-MM-DD)
   * - end_date: Return workouts on or before this date (YYYY-MM-DD)
   *
   * Requires JWT authentication
   */
  fastify.get<{ Querystring: ListWorkoutsQuery }>(
    '/workouts',
    {
      ...listWorkoutsSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Querystring: ListWorkoutsQuery }>,
      reply: FastifyReply
    ) => {
      try {
        const { start_date, end_date, workout_id } = request.query;
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // If workout_id provided, fetch specific workout
        if (workout_id !== undefined) {
          const workout = stmtGetWorkoutById.get(workout_id) as any;

          if (!workout) {
            return reply.status(404).send({ error: 'Workout not found' });
          }

          // Verify ownership
          if (workout.user_id !== authenticatedUser.userId) {
            return reply.status(403).send({ error: 'Not authorized to access this workout' });
          }

          // Get program_day info and exercises
          const programDay = db.prepare('SELECT day_name, day_type FROM program_days WHERE id = ?')
            .get(workout.program_day_id) as any;

          const exercises = db.prepare(`
            SELECT pe.*, e.name as exercise_name
            FROM program_exercises pe
            JOIN exercises e ON pe.exercise_id = e.id
            WHERE pe.program_day_id = ?
            ORDER BY pe.order_index ASC
          `).all(workout.program_day_id);

          const workoutWithDetails = {
            ...workout,
            day_name: programDay?.day_name || null,
            day_type: programDay?.day_type || null,
            exercises,
          };

          return reply.status(200).send([workoutWithDetails]);
        }

        // Otherwise, list workouts with date filters
        const workouts = listWorkouts(
          authenticatedUser.userId,
          start_date,
          end_date
        );

        return reply.status(200).send(workouts);
      } catch (error) {
        // Generic error
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to list workouts',
        });
      }
    }
  );

  /**
   * PATCH /api/workouts/:id
   *
   * Update workout status (e.g., mark as completed) or change program_day_id
   *
   * Requires JWT authentication
   */
  fastify.patch<{
    Params: { id: string };
    Body: {
      status?: 'not_started' | 'in_progress' | 'completed' | 'cancelled';
      program_day_id?: number;
    };
  }>(
    '/workouts/:id',
    {
      preHandler: authenticateJWT,
      schema: {
        params: {
          type: 'object',
          properties: {
            id: { type: 'string' },
          },
          required: ['id'],
        },
        body: {
          type: 'object',
          properties: {
            status: {
              type: 'string',
              enum: ['not_started', 'in_progress', 'completed', 'cancelled'],
            },
            program_day_id: {
              type: 'integer',
              description: 'Change the program day for this workout (only allowed if status is not_started)',
            },
          },
          minProperties: 1, // Require at least one property
        },
      },
    },
    async (request, reply) => {
      try {
        const workoutId = parseInt(request.params.id, 10);
        const { status, program_day_id } = request.body;
        const authenticatedUser = (request as AuthenticatedRequest).user;

        const workout = updateWorkoutStatus(
          authenticatedUser.userId,
          workoutId,
          status,
          program_day_id
        );
        return reply.status(200).send(workout);
      } catch (error) {
        // Handle validation errors
        if (error instanceof Error) {
          if (
            error.message.includes('not allowed') ||
            error.message.includes('Invalid') ||
            error.message.includes('does not belong')
          ) {
            return reply.status(400).send({
              error: error.message,
            });
          }
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to update workout',
        });
      }
    }
  );
}

/**
 * Set Logging Routes
 *
 * REST endpoints for exercise set tracking:
 * - POST /api/sets - Log individual exercise set with weight, reps, RIR
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { logSet, getSetsForWorkout } from '../services/setService.js';
import { authenticateJWT } from '../middleware/auth.js';

/**
 * Log set request body interface
 */
interface LogSetBody {
  workout_id: number;
  exercise_id: number;
  set_number: number;
  weight_kg: number;
  reps: number;
  rir: number;
  timestamp: number;
  localId?: number;
  notes?: string;
}

/**
 * Log set request schema
 */
const logSetSchema = {
  schema: {
    body: {
      type: 'object',
      required: [
        'workout_id',
        'exercise_id',
        'set_number',
        'weight_kg',
        'reps',
        'rir',
        'timestamp',
      ],
      properties: {
        workout_id: {
          type: 'integer',
          description: 'ID of the workout session',
        },
        exercise_id: {
          type: 'integer',
          description: 'ID of the exercise being performed',
        },
        set_number: {
          type: 'integer',
          minimum: 1,
          description: 'Set number within the workout (1, 2, 3, etc.)',
        },
        weight_kg: {
          type: 'number',
          minimum: 0,
          maximum: 500,
          description: 'Weight lifted in kilograms (0-500)',
        },
        reps: {
          type: 'integer',
          minimum: 1,
          maximum: 50,
          description: 'Number of repetitions completed (1-50)',
        },
        rir: {
          type: 'integer',
          minimum: 0,
          maximum: 4,
          description: 'Reps in Reserve (0-4)',
        },
        timestamp: {
          type: 'integer',
          description: 'UTC milliseconds when set was completed',
        },
        localId: {
          type: 'integer',
          description: 'Optional local ID from mobile app for deduplication',
        },
        notes: {
          type: 'string',
          maxLength: 500,
          description: 'Optional notes about the set (max 500 chars)',
        },
      },
    },
    response: {
      201: {
        type: 'object',
        properties: {
          id: { type: 'number' },
          localId: { type: ['number', 'null'] },
          synced: { type: 'boolean' },
          estimated_1rm: { type: 'number' },
          weight_kg: { type: 'number' },
          reps: { type: 'number' },
          rir: { type: 'number' },
        },
      },
    },
  },
};

/**
 * Register set logging routes
 */
export default async function setRoutes(fastify: FastifyInstance) {
  /**
   * GET /api/sets
   *
   * Fetch sets for a workout session
   * Query param: workout_id (required)
   *
   * Requires JWT authentication
   */
  fastify.get<{ Querystring: { workout_id?: string } }>(
    '/sets',
    { preHandler: authenticateJWT },
    async (
      request: FastifyRequest<{ Querystring: { workout_id?: string } }>,
      reply: FastifyReply
    ) => {
      try {
        const workoutId = request.query.workout_id;

        if (!workoutId) {
          return reply.status(400).send({
            error: 'workout_id query parameter is required',
          });
        }

        const sets = getSetsForWorkout(parseInt(workoutId));

        return reply.status(200).send(sets);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to fetch sets',
        });
      }
    }
  );

  /**
   * POST /api/sets
   *
   * Log an exercise set for a workout session
   * Implements idempotent sync via localId deduplication
   *
   * Validation rules (per FR-005):
   * - weight_kg: 0-500
   * - reps: 1-50
   * - rir: 0-4
   * - notes: max 500 characters
   *
   * Requires JWT authentication
   */
  fastify.post<{ Body: LogSetBody }>(
    '/sets',
    {
      ...logSetSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Body: LogSetBody }>,
      reply: FastifyReply
    ) => {
      try {
        const {
          workout_id,
          exercise_id,
          set_number,
          weight_kg,
          reps,
          rir,
          timestamp,
          localId,
          notes,
        } = request.body;

        // Log the set
        const result = logSet(
          workout_id,
          exercise_id,
          set_number,
          weight_kg,
          reps,
          rir,
          timestamp,
          localId,
          notes
        );

        return reply.status(201).send(result);
      } catch (error) {
        // Handle validation errors
        if (
          error instanceof Error &&
          (error.message.includes('must be between') ||
            error.message.includes('must be 500 characters'))
        ) {
          return reply.status(400).send({
            error: error.message,
          });
        }

        // Handle database errors
        if (
          error instanceof Error &&
          error.message.includes('FOREIGN KEY constraint failed')
        ) {
          return reply.status(400).send({
            error: 'Invalid workout_id or exercise_id',
          });
        }

        // Generic error
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to log set',
        });
      }
    }
  );
}

/**
 * Recovery Assessment Routes
 *
 * REST endpoints for daily recovery check-in and auto-regulation:
 * - POST /api/recovery-assessments - Submit daily 3-question assessment
 * - GET /api/recovery-assessments/:userId/today - Get today's assessment
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { createAssessment } from '../services/recoveryService.js';
import { authenticateJWT } from '../middleware/auth.js';
import { db } from '../database/db.js';

/**
 * Recovery assessment request body interface
 */
interface CreateAssessmentBody {
  date: string;
  sleep_quality: number;
  muscle_soreness: number;
  mental_motivation: number;
}

/**
 * Recovery assessment request schema
 */
const createAssessmentSchema = {
  schema: {
    body: {
      type: 'object',
      required: ['date', 'sleep_quality', 'muscle_soreness', 'mental_motivation'],
      properties: {
        date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
          description: 'Assessment date in ISO format (YYYY-MM-DD)',
        },
        sleep_quality: {
          type: 'integer',
          minimum: 1,
          maximum: 5,
          description: 'Sleep quality rating (1-5 scale)',
        },
        muscle_soreness: {
          type: 'integer',
          minimum: 1,
          maximum: 5,
          description: 'Muscle soreness rating (1-5 scale)',
        },
        mental_motivation: {
          type: 'integer',
          minimum: 1,
          maximum: 5,
          description: 'Mental motivation rating (1-5 scale)',
        },
      },
    },
    response: {
      201: {
        type: 'object',
        properties: {
          total_score: { type: 'number' },
          volume_adjustment: {
            type: 'string',
            enum: ['none', 'reduce_1_set', 'reduce_2_sets', 'rest_day'],
          },
        },
      },
    },
  },
};

/**
 * Register recovery assessment routes
 */
export default async function recoveryRoutes(fastify: FastifyInstance) {
  /**
   * POST /api/recovery-assessments
   *
   * Create a daily recovery assessment for auto-regulation
   *
   * Calculates total score from 3 subscores (sleep, soreness, motivation)
   * and determines volume adjustment per FR-009:
   * - 12-15: No adjustment
   * - 9-11: Reduce by 1 set per exercise
   * - 6-8: Reduce by 2 sets per exercise
   * - 3-5: Rest day recommended
   *
   * Validation rules (per FR-008):
   * - date: ISO format YYYY-MM-DD
   * - sleep_quality: 1-5
   * - muscle_soreness: 1-5
   * - mental_motivation: 1-5
   *
   * Requires JWT authentication
   */
  fastify.post<{ Body: CreateAssessmentBody }>(
    '/recovery-assessments',
    {
      ...createAssessmentSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Body: CreateAssessmentBody }>,
      reply: FastifyReply
    ) => {
      try {
        const { date, sleep_quality, muscle_soreness, mental_motivation } = request.body;

        // Get authenticated user ID
        // @ts-expect-error - jwtVerify attaches user to request
        const userId = request.user.userId;

        // Create recovery assessment
        const result = createAssessment(
          userId,
          date,
          sleep_quality,
          muscle_soreness,
          mental_motivation
        );

        return reply.status(201).send(result);
      } catch (error) {
        // Handle validation errors
        if (
          error instanceof Error &&
          (error.message.includes('must be between') ||
            error.message.includes('must be in ISO format'))
        ) {
          return reply.status(400).send({
            error: error.message,
          });
        }

        // Handle database errors
        if (
          error instanceof Error &&
          error.message.includes('FOREIGN KEY constraint failed')
        ) {
          return reply.status(400).send({
            error: 'Invalid user_id',
          });
        }

        // Handle unique constraint violations (duplicate assessment for date)
        if (
          error instanceof Error &&
          error.message.includes('UNIQUE constraint failed')
        ) {
          return reply.status(400).send({
            error: 'Recovery assessment already exists for this date',
          });
        }

        // Generic error
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to create recovery assessment',
        });
      }
    }
  );

  /**
   * GET /api/recovery-assessments/:userId/today
   *
   * Get today's recovery assessment for a user
   * Returns 404 if no assessment found for today
   *
   * Requires JWT authentication
   */
  fastify.get<{ Params: { userId: string } }>(
    '/recovery-assessments/:userId/today',
    {
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Params: { userId: string } }>,
      reply: FastifyReply
    ) => {
      try {
        const userId = parseInt(request.params.userId);

        // Get authenticated user ID
        // @ts-expect-error - jwtVerify attaches user to request
        const authUserId = request.user.userId;

        // Verify user can only access their own data
        if (userId !== authUserId) {
          return reply.status(403).send({
            error: 'Forbidden: Cannot access other users data',
          });
        }

        // Get today's date
        const today = new Date().toISOString().split('T')[0];

        // Query database
        const assessment = db
          .prepare(
            `SELECT * FROM recovery_assessments
             WHERE user_id = ? AND date = ?`
          )
          .get(userId, today);

        if (!assessment) {
          return reply.status(404).send({
            error: 'No assessment found for today',
          });
        }

        return reply.status(200).send(assessment);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to fetch recovery assessment',
        });
      }
    }
  );
}

/**
 * Analytics Routes (T050-T052)
 *
 * Endpoints for 1RM progression, volume trends, and consistency metrics
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import {
  get1RMProgression,
  getConsistencyMetrics,
} from '../services/analyticsService.js';
import {
  getCurrentWeekVolume,
  getVolumeHistory,
  getProgramVolumeAnalysis,
} from '../services/volumeService.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';

/**
 * Query params for 1RM progression
 */
interface OneRMProgressionQuery {
  exercise_id: string;
  start_date: string;
  end_date: string;
}

/**
 * Schema for 1RM progression endpoint
 */
const oneRMProgressionSchema = {
  schema: {
    querystring: {
      type: 'object',
      required: ['exercise_id', 'start_date', 'end_date'],
      properties: {
        exercise_id: {
          type: 'string',
          description: 'Exercise ID',
        },
        start_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
          description: 'Start date (YYYY-MM-DD)',
        },
        end_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
          description: 'End date (YYYY-MM-DD)',
        },
      },
    },
    response: {
      200: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            date: { type: 'string' },
            estimated_1rm: { type: 'number' },
          },
        },
      },
    },
  },
};

/**
 * Schema for consistency metrics endpoint
 */
const consistencyMetricsSchema = {
  schema: {
    response: {
      200: {
        type: 'object',
        properties: {
          adherence_rate: { type: 'number' },
          avg_session_duration: { type: 'number' },
          total_workouts: { type: 'number' },
        },
      },
    },
  },
};

/**
 * Register analytics routes
 */
export default async function analyticsRoutes(fastify: FastifyInstance) {
  /**
   * GET /api/analytics/1rm-progression (T050)
   *
   * Get estimated 1RM progression for an exercise over time
   *
   * Query params:
   * - exercise_id: Exercise ID
   * - start_date: Start date (YYYY-MM-DD)
   * - end_date: End date (YYYY-MM-DD)
   *
   * Returns: Array of {date, estimated_1rm}
   */
  fastify.get<{ Querystring: OneRMProgressionQuery }>(
    '/analytics/1rm-progression',
    {
      ...oneRMProgressionSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Querystring: OneRMProgressionQuery }>,
      reply: FastifyReply
    ) => {
      try {
        const { exercise_id, start_date, end_date } = request.query;
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Convert exercise_id to number
        const exerciseId = parseInt(exercise_id, 10);
        if (isNaN(exerciseId)) {
          return reply.status(400).send({
            error: 'Invalid exercise_id',
          });
        }

        // Call analytics service
        const progression = get1RMProgression(
          authenticatedUser.userId,
          exerciseId,
          start_date,
          end_date
        );

        return reply.status(200).send(progression);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve 1RM progression',
        });
      }
    }
  );

  /**
   * GET /api/analytics/consistency (T052)
   *
   * Get consistency metrics for the authenticated user
   *
   * Returns: {adherence_rate, avg_session_duration, total_workouts}
   */
  fastify.get(
    '/analytics/consistency',
    {
      ...consistencyMetricsSchema,
      preHandler: authenticateJWT,
    },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Call analytics service
        const metrics = getConsistencyMetrics(authenticatedUser.userId);

        return reply.status(200).send(metrics);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve consistency metrics',
        });
      }
    }
  );

  /**
   * GET /api/analytics/volume-current-week (T017)
   *
   * Get current week volume tracking with completed and planned sets
   *
   * Returns: {week_start, week_end, muscle_groups: [{muscle_group, completed_sets, planned_sets, ...}]}
   */
  fastify.get(
    '/analytics/volume-current-week',
    {
      preHandler: authenticateJWT,
    },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Call volume service
        const volumeData = getCurrentWeekVolume(authenticatedUser.userId);

        return reply.status(200).send(volumeData);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve current week volume',
        });
      }
    }
  );

  /**
   * GET /api/analytics/volume-trends (T018)
   *
   * Get historical volume trends over multiple weeks
   *
   * Query params:
   * - weeks: Number of weeks (default: 8, max: 52)
   * - muscle_group: Optional filter for specific muscle group
   *
   * Returns: {weeks: [{week_start, muscle_groups: [{muscle_group, completed_sets, mev, mav, mrv}]}]}
   */
  fastify.get<{ Querystring: { weeks?: string; muscle_group?: string } }>(
    '/analytics/volume-trends',
    {
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Querystring: { weeks?: string; muscle_group?: string } }>,
      reply: FastifyReply
    ) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const { weeks: weeksParam, muscle_group } = request.query;

        // Parse weeks parameter
        let weeks = 8; // Default
        if (weeksParam) {
          weeks = parseInt(weeksParam, 10);
          if (isNaN(weeks) || weeks < 1) {
            return reply.status(400).send({
              error: 'Invalid weeks parameter. Must be a positive number.',
            });
          }
          if (weeks > 52) {
            return reply.status(400).send({
              error: 'Weeks parameter exceeds maximum of 52',
            });
          }
        }

        // Validate muscle_group parameter
        if (muscle_group) {
          const validMuscleGroups = [
            'chest', 'back', 'shoulders', 'quads', 'hamstrings',
            'glutes', 'biceps', 'triceps', 'calves', 'abs',
            'back_lats', 'back_traps', 'shoulders_front', 'shoulders_side', 'shoulders_rear',
            'front_delts', 'side_delts', 'rear_delts'
          ];

          if (!validMuscleGroups.includes(muscle_group)) {
            return reply.status(400).send({
              error: 'Invalid muscle_group parameter',
            });
          }
        }

        // Call volume service
        const volumeHistory = getVolumeHistory(authenticatedUser.userId, weeks, muscle_group);

        return reply.status(200).send(volumeHistory);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve volume trends',
        });
      }
    }
  );

  /**
   * GET /api/analytics/program-volume-analysis (T019)
   *
   * Get program volume analysis for active program
   *
   * Returns: {program_id, mesocycle_phase, muscle_groups: [{muscle_group, planned_weekly_sets, zone, ...}]}
   */
  fastify.get(
    '/analytics/program-volume-analysis',
    {
      preHandler: authenticateJWT,
    },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Call volume service
        const programAnalysis = getProgramVolumeAnalysis(authenticatedUser.userId);

        if (!programAnalysis) {
          return reply.status(404).send({
            error: 'No active program found for user',
          });
        }

        return reply.status(200).send(programAnalysis);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve program volume analysis',
        });
      }
    }
  );
}

/**
 * Program Days Routes
 *
 * REST endpoints for viewing training program structure:
 * - GET /api/program-days - List all program days for user's current program
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { db } from '../database/db.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';

/**
 * Program day response interface
 */
interface ProgramDayResponse {
  id: number;
  program_id: number;
  day_of_week: number;
  day_name: string;
  day_type: 'strength' | 'vo2max';
  exercise_count: number;
}

/**
 * Program exercise interface
 */
interface ProgramExercise {
  id: number;
  program_day_id: number;
  exercise_id: number;
  order_index: number;
  sets: number;
  reps: string;
  rir: number;
  exercise_name: string;
  muscle_groups: string;
  equipment: string;
}

/**
 * Recommended program day response interface
 */
interface RecommendedProgramDayResponse {
  id: number;
  program_id: number;
  day_of_week: number;
  day_name: string;
  day_type: 'strength' | 'vo2max';
  exercises: ProgramExercise[];
}

/**
 * List program days response schema
 */
const listProgramDaysSchema = {
  schema: {
    response: {
      200: {
        type: 'array',
        items: {
          type: 'object',
          properties: {
            id: { type: 'number' },
            program_id: { type: 'number' },
            day_of_week: { type: 'number' },
            day_name: { type: 'string' },
            day_type: {
              type: 'string',
              enum: ['strength', 'vo2max'],
            },
            exercise_count: { type: 'number' },
          },
        },
      },
    },
  },
};

/**
 * Recommended program day response schema
 */
const recommendedProgramDaySchema = {
  schema: {
    response: {
      200: {
        type: 'object',
        properties: {
          id: { type: 'number' },
          program_id: { type: 'number' },
          day_of_week: { type: 'number' },
          day_name: { type: 'string' },
          day_type: {
            type: 'string',
            enum: ['strength', 'vo2max'],
          },
          exercises: {
            type: 'array',
            items: {
              type: 'object',
              properties: {
                id: { type: 'number' },
                program_day_id: { type: 'number' },
                exercise_id: { type: 'number' },
                order_index: { type: 'number' },
                sets: { type: 'number' },
                reps: { type: 'string' },
                rir: { type: 'number' },
                exercise_name: { type: 'string' },
                muscle_groups: { type: 'string' },
                equipment: { type: 'string' },
              },
            },
          },
        },
      },
    },
  },
};

/**
 * Register program days routes
 */
export default async function programDaysRoutes(fastify: FastifyInstance) {
  /**
   * GET /api/program-days
   *
   * List all program days for the authenticated user's current program
   * Returns each day with its exercise count for easy workout swapping
   *
   * Requires JWT authentication
   */
  fastify.get(
    '/program-days',
    {
      ...listProgramDaysSchema,
      preHandler: authenticateJWT,
    },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Get user's most recent program
        const programStmt = db.prepare(`
          SELECT id FROM programs
          WHERE user_id = ?
          ORDER BY created_at DESC
          LIMIT 1
        `);
        const program = programStmt.get(authenticatedUser.userId) as { id: number } | undefined;

        if (!program) {
          return reply.status(404).send({
            error: 'No program found for user',
          });
        }

        // Get all program days with exercise counts
        const programDaysStmt = db.prepare(`
          SELECT
            pd.id,
            pd.program_id,
            pd.day_of_week,
            pd.day_name,
            pd.day_type,
            COUNT(pe.id) as exercise_count
          FROM program_days pd
          LEFT JOIN program_exercises pe ON pd.id = pe.program_day_id
          WHERE pd.program_id = ?
          GROUP BY pd.id, pd.program_id, pd.day_of_week, pd.day_name, pd.day_type
          ORDER BY pd.day_of_week
        `);

        const programDays = programDaysStmt.all(program.id) as ProgramDayResponse[];

        return reply.status(200).send(programDays);
      } catch (error) {
        // Generic error
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to list program days',
        });
      }
    }
  );

  /**
   * GET /api/program-days/recommended
   *
   * Get recommended program day for today based on current day of week
   * Returns the program day matching today's weekday with all exercises
   *
   * Requires JWT authentication
   */
  fastify.get(
    '/program-days/recommended',
    {
      ...recommendedProgramDaySchema,
      preHandler: authenticateJWT,
    },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Get user's most recent program
        const programStmt = db.prepare(`
          SELECT id FROM programs
          WHERE user_id = ?
          ORDER BY created_at DESC
          LIMIT 1
        `);
        const program = programStmt.get(authenticatedUser.userId) as { id: number } | undefined;

        if (!program) {
          return reply.status(404).send({
            error: 'No program found for user',
          });
        }

        // Determine today's day of week (0=Sunday, 1=Monday, ..., 6=Saturday)
        const dayOfWeek = new Date().getDay();

        // Map day of week to program day_of_week
        // Sunday (0) -> day 6 (index 5, VO2max B)
        // Monday (1) -> day 1 (index 0, Push A)
        // Tuesday (2) -> day 2 (index 1, Pull A)
        // Wednesday (3) -> day 3 (index 2, VO2max A)
        // Thursday (4) -> day 4 (index 3, Push B)
        // Friday (5) -> day 5 (index 4, Pull B)
        // Saturday (6) -> day 6 (index 5, VO2max B)
        const dayMapping = [6, 1, 2, 3, 4, 5, 6]; // Maps JS day of week to program day_of_week
        const targetDayOfWeek = dayMapping[dayOfWeek]!;

        // Get program day for today
        const programDayStmt = db.prepare(`
          SELECT
            id,
            program_id,
            day_of_week,
            day_name,
            day_type
          FROM program_days
          WHERE program_id = ? AND day_of_week = ?
        `);

        const programDay = programDayStmt.get(program.id, targetDayOfWeek) as Omit<RecommendedProgramDayResponse, 'exercises'> | undefined;

        if (!programDay) {
          return reply.status(404).send({
            error: 'No program day found for today',
          });
        }

        // Get exercises for this program day
        const exercisesStmt = db.prepare(`
          SELECT
            pe.id,
            pe.program_day_id,
            pe.exercise_id,
            pe.order_index,
            pe.sets,
            pe.reps,
            pe.rir,
            e.name as exercise_name,
            e.muscle_groups,
            e.equipment
          FROM program_exercises pe
          JOIN exercises e ON pe.exercise_id = e.id
          WHERE pe.program_day_id = ?
          ORDER BY pe.order_index
        `);

        const exercises = exercisesStmt.all(programDay.id) as ProgramExercise[];

        const response: RecommendedProgramDayResponse = {
          ...programDay,
          exercises,
        };

        return reply.status(200).send(response);
      } catch (error) {
        // Generic error
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to get recommended program day',
        });
      }
    }
  );
}

/**
 * Exercise Library Routes
 *
 * Endpoints for filtering and retrieving exercises
 */

import { FastifyInstance } from 'fastify';
import { getExercises, getExerciseById, ExerciseFilters } from '../services/exerciseService.js';
import { authenticateJWT } from '../middleware/auth.js';

/**
 * Query parameters for GET /api/exercises
 */
interface ExerciseQueryParams {
  muscle_group?: string;
  equipment?: 'barbell' | 'dumbbell' | 'cable' | 'machine' | 'bodyweight';
  movement_pattern?: 'compound' | 'isolation';
  difficulty?: 'beginner' | 'intermediate' | 'advanced';
}

/**
 * Route parameters for GET /api/exercises/:id
 */
interface ExerciseParams {
  id: string;
}

/**
 * Register exercise routes
 */
export default async function exerciseRoutes(fastify: FastifyInstance) {
  /**
   * GET /api/exercises
   *
   * Get exercises with optional filtering
   */
  fastify.get(
    '/',
    {
      preHandler: authenticateJWT,
      schema: {
        querystring: {
          type: 'object',
          properties: {
            muscle_group: {
              type: 'string',
              description: 'Filter by muscle group (chest, lats, quads, etc.)',
            },
            equipment: {
              type: 'string',
              enum: ['barbell', 'dumbbell', 'cable', 'machine', 'bodyweight'],
              description: 'Filter by equipment type',
            },
            movement_pattern: {
              type: 'string',
              enum: ['compound', 'isolation'],
              description: 'Filter by movement pattern',
            },
            difficulty: {
              type: 'string',
              enum: ['beginner', 'intermediate', 'advanced'],
              description: 'Filter by difficulty level',
            },
          },
        },
        response: {
          200: {
            type: 'object',
            properties: {
              exercises: {
                type: 'array',
                items: {
                  type: 'object',
                  properties: {
                    id: { type: 'number' },
                    name: { type: 'string' },
                    primary_muscle_group: { type: 'string' },
                    secondary_muscle_groups: {
                      type: 'array',
                      items: { type: 'string' },
                    },
                    equipment: { type: 'string' },
                    movement_pattern: { type: 'string' },
                    difficulty: { type: 'string' },
                    default_sets: { type: 'number' },
                    default_reps: { type: 'string' },
                    default_rir: { type: 'number' },
                    description: { type: 'string' },
                  },
                },
              },
              count: { type: 'number' },
            },
          },
        },
      },
    },
    async (request, reply) => {
      const { muscle_group, equipment, movement_pattern, difficulty } = request.query as ExerciseQueryParams;
      try {
        const filters: ExerciseFilters = {
          muscle_group,
          equipment,
          movement_pattern,
          difficulty,
        };

        const exercises = getExercises(filters);

        return reply.status(200).send({
          exercises,
          count: exercises.length,
        });
      } catch (error: any) {
        // Handle validation errors (e.g., invalid muscle_group)
        if (error.message?.includes('Invalid muscle_group')) {
          return reply.status(400).send({
            error: error.message,
          });
        }

        // Handle unexpected errors
        request.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve exercises',
        });
      }
    }
  );

  /**
   * GET /api/exercises/:id
   *
   * Get exercise details by ID
   */
  fastify.get(
    '/:id',
    {
      preHandler: authenticateJWT,
      schema: {
        params: {
          type: 'object',
          required: ['id'],
          properties: {
            id: {
              type: 'string',
              description: 'Exercise ID',
            },
          },
        },
        response: {
          200: {
            type: 'object',
            properties: {
              id: { type: 'number' },
              name: { type: 'string' },
              primary_muscle_group: { type: 'string' },
              secondary_muscle_groups: {
                type: 'array',
                items: { type: 'string' },
              },
              equipment: { type: 'string' },
              movement_pattern: { type: 'string' },
              difficulty: { type: 'string' },
              default_sets: { type: 'number' },
              default_reps: { type: 'string' },
              default_rir: { type: 'number' },
              description: { type: 'string' },
            },
          },
          404: {
            type: 'object',
            properties: {
              error: { type: 'string' },
            },
          },
        },
      },
    },
    async (request, reply) => {
      const { id } = request.params as ExerciseParams;
      try {
        const exerciseId = parseInt(id, 10);

        if (isNaN(exerciseId)) {
          return reply.status(400).send({
            error: 'Invalid exercise ID',
          });
        }

        const exercise = getExerciseById(exerciseId);

        if (!exercise) {
          return reply.status(404).send({
            error: 'Exercise not found',
          });
        }

        return reply.status(200).send(exercise);
      } catch (error: any) {
        request.log.error(error);
        return reply.status(500).send({
          error: 'Failed to retrieve exercise',
        });
      }
    }
  );
}

/**
 * Program Routes (T050-T054)
 *
 * REST endpoints for training program management:
 * - GET /api/programs - Get user's active program with full structure
 * - PATCH /api/programs/:id/advance-phase - Advance mesocycle phase with volume adjustment
 * - GET /api/programs/:id/volume - Get program volume analysis per muscle group
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import {
  getUserProgram,
  getProgramDays,
  getProgramDayExercises,
  advancePhase,
} from '../services/programService.js';
import { getProgramVolumeAnalysis } from '../services/volumeService.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';

/**
 * Advance phase request body interface
 */
interface AdvancePhaseBody {
  manual?: boolean;
  target_phase?: 'mev' | 'mav' | 'mrv' | 'deload';
}

/**
 * GET /api/programs schema
 */
const getProgramsSchema = {
  schema: {
    response: {
      200: {
        type: 'object',
        properties: {
          id: { type: 'number' },
          user_id: { type: 'number' },
          name: { type: 'string' },
          mesocycle_week: { type: 'number' },
          mesocycle_phase: {
            type: 'string',
            enum: ['mev', 'mav', 'mrv', 'deload'],
          },
          created_at: { type: 'number' },
          program_days: {
            type: 'array',
            items: {
              type: 'object',
              properties: {
                id: { type: 'number' },
                program_id: { type: 'number' },
                day_of_week: { type: 'number' },
                day_name: { type: 'string' },
                day_type: {
                  type: 'string',
                  enum: ['strength', 'vo2max'],
                },
                exercises: {
                  type: 'array',
                  items: {
                    type: 'object',
                    properties: {
                      id: { type: 'number' },
                      program_day_id: { type: 'number' },
                      exercise_id: { type: 'number' },
                      exercise_name: { type: 'string' },
                      order_index: { type: 'number' },
                      target_sets: { type: 'number' },
                      target_rep_range: { type: 'string' },
                      target_rir: { type: 'number' },
                      muscle_groups: { type: 'string' },
                      equipment: { type: 'string' },
                    },
                  },
                },
              },
            },
          },
        },
      },
      404: {
        type: 'object',
        properties: {
          error: { type: 'string' },
          message: { type: 'string' },
        },
      },
    },
  },
};

/**
 * PATCH /api/programs/:id/advance-phase schema
 */
const advancePhaseSchema = {
  schema: {
    params: {
      type: 'object',
      properties: {
        id: { type: 'string' },
      },
      required: ['id'],
    },
    body: {
      type: 'object',
      properties: {
        manual: { type: 'boolean' },
        target_phase: {
          type: 'string',
          enum: ['mev', 'mav', 'mrv', 'deload'],
        },
      },
    },
    response: {
      200: {
        type: 'object',
        properties: {
          previous_phase: { type: 'string' },
          new_phase: { type: 'string' },
          volume_multiplier: { type: 'number' },
          exercises_updated: { type: 'number' },
        },
      },
    },
  },
};

/**
 * GET /api/programs/:id/volume schema
 */
const getVolumeSchema = {
  schema: {
    params: {
      type: 'object',
      properties: {
        id: { type: 'string' },
      },
      required: ['id'],
    },
    response: {
      200: {
        type: 'object',
        properties: {
          muscle_groups: {
            type: 'array',
            items: {
              type: 'object',
              properties: {
                muscle_group: { type: 'string' },
                planned_sets: { type: 'number' },
                mev: { type: 'number' },
                mav: { type: 'number' },
                mrv: { type: 'number' },
                zone: {
                  type: 'string',
                  enum: ['below_mev', 'adequate', 'optimal', 'above_mrv'],
                },
              },
            },
          },
          warnings: {
            type: 'array',
            items: {
              type: 'object',
              properties: {
                muscle_group: { type: 'string' },
                issue: {
                  type: 'string',
                  enum: ['below_mev', 'above_mrv'],
                },
                current_volume: { type: 'number' },
                threshold: { type: 'number' },
              },
            },
          },
        },
      },
    },
  },
};

/**
 * Register program routes
 */
export default async function programRoutes(fastify: FastifyInstance) {
  /**
   * GET /api/programs
   *
   * Get the authenticated user's active program with full structure
   * (program days and exercises nested)
   *
   * Returns 404 if user has no active program
   * Requires JWT authentication
   */
  fastify.get(
    '/programs',
    {
      ...getProgramsSchema,
      preHandler: authenticateJWT,
    },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Get user's active program
        const program = getUserProgram(authenticatedUser.userId);

        if (!program) {
          return reply.status(404).send({
            error: 'Not Found',
            message: 'No active program found for user',
          });
        }

        // Get program days
        const programDays = getProgramDays(program.id);

        // Get exercises for each program day
        const programDaysWithExercises = programDays.map((day: any) => {
          const exercises = getProgramDayExercises(day.id);

          // Transform to match contract schema
          const transformedExercises = exercises.map((ex: any) => ({
            id: ex.id,
            program_day_id: ex.program_day_id,
            exercise_id: ex.exercise_id,
            exercise_name: ex.exercise_name,
            order_index: ex.order_index,
            target_sets: ex.sets, // Contract uses target_sets
            target_rep_range: ex.reps, // Contract uses target_rep_range
            target_rir: ex.rir, // Contract uses target_rir
            muscle_groups: ex.muscle_groups,
            equipment: ex.equipment,
          }));

          return {
            ...day,
            exercises: transformedExercises,
          };
        });

        // Build full response
        const response = {
          ...program,
          program_days: programDaysWithExercises,
        };

        return reply.status(200).send(response);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Internal Server Error',
          message: 'Failed to retrieve program',
        });
      }
    }
  );

  /**
   * PATCH /api/programs/:id/advance-phase
   *
   * Advance program to next mesocycle phase with automatic volume adjustment
   *
   * Phase progression: mev  mav  mrv  deload  mev (repeats)
   * Volume multipliers:
   * - MEV  MAV: 1.2x (+20%)
   * - MAV  MRV: 1.15x (+15%)
   * - MRV  Deload: 0.5x (-50%)
   * - Deload  MEV: 2.0x (reset to baseline)
   *
   * Requires JWT authentication
   * Validates user owns the program before modification
   */
  fastify.patch<{
    Params: { id: string };
    Body: AdvancePhaseBody;
  }>(
    '/programs/:id/advance-phase',
    {
      ...advancePhaseSchema,
      preHandler: authenticateJWT,
    },
    async (request, reply) => {
      try {
        const programId = parseInt(request.params.id, 10);
        const { manual, target_phase } = request.body;
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Validate manual mode
        if (manual === true && !target_phase) {
          return reply.status(400).send({
            error: 'Bad Request',
            message: 'target_phase is required when manual=true',
          });
        }

        // Validate target_phase
        if (target_phase && !['mev', 'mav', 'mrv', 'deload'].includes(target_phase)) {
          return reply.status(400).send({
            error: 'Bad Request',
            message: `Invalid target_phase: ${target_phase}`,
          });
        }

        // Verify program exists and belongs to user
        const program = getUserProgram(authenticatedUser.userId);

        if (!program) {
          return reply.status(404).send({
            error: 'Not Found',
            message: 'Program not found',
          });
        }

        if (program.id !== programId) {
          return reply.status(404).send({
            error: 'Not Found',
            message: 'Program not found',
          });
        }

        // Advance phase
        const result = advancePhase(programId, manual || false, target_phase);

        return reply.status(200).send(result);
      } catch (error) {
        if (error instanceof Error) {
          // Handle validation errors
          if (
            error.message.includes('required') ||
            error.message.includes('Invalid') ||
            error.message.includes('not found')
          ) {
            return reply.status(400).send({
              error: 'Bad Request',
              message: error.message,
            });
          }
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Internal Server Error',
          message: 'Failed to advance phase',
        });
      }
    }
  );

  /**
   * GET /api/programs/:id/volume
   *
   * Get volume analysis for the program (planned sets per muscle group)
   * Classifies volume zones based on MEV/MAV/MRV landmarks
   *
   * Zones:
   * - below_mev: Planned sets < MEV (insufficient for growth)
   * - adequate: MEV  planned < MAV (adequate stimulus)
   * - optimal: MAV  planned  MRV (optimal range)
   * - above_mrv: Planned > MRV (risk of overtraining)
   *
   * Requires JWT authentication
   * Validates user owns the program
   */
  fastify.get<{
    Params: { id: string };
  }>(
    '/programs/:id/volume',
    {
      ...getVolumeSchema,
      preHandler: authenticateJWT,
    },
    async (request, reply) => {
      try {
        const programId = parseInt(request.params.id, 10);
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Verify program exists and belongs to user
        const program = getUserProgram(authenticatedUser.userId);

        if (!program) {
          return reply.status(404).send({
            error: 'Not Found',
            message: 'Program not found',
          });
        }

        if (program.id !== programId) {
          return reply.status(404).send({
            error: 'Not Found',
            message: 'Program not found',
          });
        }

        // Get volume analysis
        const volumeAnalysis = getProgramVolumeAnalysis(authenticatedUser.userId);

        if (!volumeAnalysis) {
          return reply.status(404).send({
            error: 'Not Found',
            message: 'No program found for volume analysis',
          });
        }

        // Transform to match contract schema
        const muscleGroups = volumeAnalysis.muscle_groups.map((mg) => ({
          muscle_group: mg.muscle_group,
          planned_sets: mg.planned_weekly_sets,
          mev: mg.mev,
          mav: mg.mav,
          mrv: mg.mrv,
          zone: mg.zone,
        }));

        // Generate warnings
        const warnings = volumeAnalysis.muscle_groups
          .filter((mg) => mg.zone === 'below_mev' || mg.zone === 'above_mrv')
          .map((mg) => ({
            muscle_group: mg.muscle_group,
            issue: mg.zone as 'below_mev' | 'above_mrv',
            current_volume: mg.planned_weekly_sets,
            threshold: mg.zone === 'below_mev' ? mg.mev : mg.mrv,
          }));

        return reply.status(200).send({
          muscle_groups: muscleGroups,
          warnings,
        });
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Internal Server Error',
          message: 'Failed to retrieve volume analysis',
        });
      }
    }
  );
}

/**
 * Program Exercise Routes (T055-T061)
 *
 * REST endpoints for managing program exercises:
 * - GET /api/program-exercises - List with optional filters
 * - POST /api/program-exercises - Add exercise to program day
 * - GET /api/program-exercises/:id - Get single program exercise
 * - PATCH /api/program-exercises/:id - Update sets/reps/RIR
 * - DELETE /api/program-exercises/:id - Remove exercise
 * - PUT /api/program-exercises/:id/swap - Swap exercise
 * - PATCH /api/program-exercises/batch-reorder - Reorder exercises
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import { db } from '../database/db.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';
import * as programExerciseService from '../services/programExerciseService.js';

/**
 * Validate user ownership of program exercise (via program_days -> programs -> user_id)
 */
function validateUserOwnership(programExerciseId: number, userId: number): boolean {
  const stmt = db.prepare(`
    SELECT pe.id
    FROM program_exercises pe
    JOIN program_days pd ON pe.program_day_id = pd.id
    JOIN programs p ON pd.program_id = p.id
    WHERE pe.id = ? AND p.user_id = ?
  `);

  const result = stmt.get(programExerciseId, userId);
  return result !== undefined;
}

/**
 * Validate user ownership of program day
 */
function validateProgramDayOwnership(programDayId: number, userId: number): boolean {
  const stmt = db.prepare(`
    SELECT pd.id
    FROM program_days pd
    JOIN programs p ON pd.program_id = p.id
    WHERE pd.id = ? AND p.user_id = ?
  `);

  const result = stmt.get(programDayId, userId);
  return result !== undefined;
}

/**
 * Request body for creating program exercise
 */
interface CreateProgramExerciseRequest {
  program_day_id: number;
  exercise_id: number;
  target_sets: number;
  target_rep_range: string;
  target_rir: number;
  order_index?: number;
}

/**
 * Request body for updating program exercise
 */
interface UpdateProgramExerciseRequest {
  target_sets?: number;
  target_rep_range?: string;
  target_rir?: number;
}

/**
 * Request body for swapping exercise
 */
interface SwapExerciseRequest {
  new_exercise_id: number;
}

/**
 * Request body for batch reorder
 */
interface BatchReorderRequest {
  program_day_id: number;
  exercise_order: Array<{
    program_exercise_id: number;
    new_order_index: number;
  }>;
}

/**
 * Register program exercise routes
 */
export default async function programExerciseRoutes(fastify: FastifyInstance) {
  /**
   * GET /api/program-exercises
   *
   * List program exercises with optional filters
   * Query params: program_day_id, exercise_id
   *
   * Requires JWT authentication
   */
  fastify.get(
    '/program-exercises',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const query = request.query as { program_day_id?: string; exercise_id?: string };

        // Build filters
        const filters: any = {};

        if (query.program_day_id) {
          const programDayId = parseInt(query.program_day_id, 10);

          // Validate user owns this program day
          if (!validateProgramDayOwnership(programDayId, authenticatedUser.userId)) {
            return reply.status(403).send({
              error: 'Access denied to this program day',
            });
          }

          filters.program_day_id = programDayId;
        }

        if (query.exercise_id) {
          filters.exercise_id = parseInt(query.exercise_id, 10);
        }

        const exercises = programExerciseService.getProgramExercises(filters);

        return reply.status(200).send({ exercises });
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to list program exercises',
        });
      }
    }
  );

  /**
   * POST /api/program-exercises
   *
   * Add exercise to program day with volume warning
   *
   * Body: { program_day_id, exercise_id, target_sets, target_rep_range, target_rir, order_index? }
   * Response: { program_exercise_id, volume_warning? }
   *
   * Requires JWT authentication
   */
  fastify.post(
    '/program-exercises',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const body = request.body as CreateProgramExerciseRequest;

        // Validate required fields
        if (!body.program_day_id || !body.exercise_id || body.target_sets === undefined || !body.target_rep_range || body.target_rir === undefined) {
          return reply.status(400).send({
            error: 'Missing required fields: program_day_id, exercise_id, target_sets, target_rep_range, target_rir',
          });
        }

        // Validate target_sets
        if (body.target_sets < 1 || body.target_sets > 10) {
          return reply.status(400).send({
            error: 'target_sets must be between 1 and 10',
          });
        }

        // Validate target_rep_range format (N-M)
        if (!/^\d+-\d+$/.test(body.target_rep_range)) {
          return reply.status(400).send({
            error: 'target_rep_range must be in format "N-M" (e.g., "8-12")',
          });
        }

        // Validate target_rir
        if (body.target_rir < 0 || body.target_rir > 4) {
          return reply.status(400).send({
            error: 'target_rir must be between 0 and 4',
          });
        }

        // Validate user owns this program day
        if (!validateProgramDayOwnership(body.program_day_id, authenticatedUser.userId)) {
          return reply.status(404).send({
            error: 'Program day not found',
          });
        }

        // Call service (this will throw if exercise_id or program_day_id doesn't exist)
        const result = programExerciseService.createProgramExercise({
          program_day_id: body.program_day_id,
          exercise_id: body.exercise_id,
          target_sets: body.target_sets,
          target_rep_range: body.target_rep_range,
          target_rir: body.target_rir,
          order_index: body.order_index,
        });

        return reply.status(201).send(result);
      } catch (error: any) {
        // Check for specific error messages from service
        if (error.message?.includes('not found')) {
          return reply.status(404).send({
            error: error.message,
          });
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to create program exercise',
        });
      }
    }
  );

  /**
   * GET /api/program-exercises/:id
   *
   * Get single program exercise
   *
   * Requires JWT authentication
   */
  fastify.get(
    '/program-exercises/:id',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const params = request.params as { id: string };
        const programExerciseId = parseInt(params.id, 10);

        // Validate user ownership
        if (!validateUserOwnership(programExerciseId, authenticatedUser.userId)) {
          return reply.status(404).send({
            error: 'Program exercise not found',
          });
        }

        // Get program exercise
        const exercises = programExerciseService.getProgramExercises({});
        const programExercise = exercises.find((ex: any) => ex.id === programExerciseId);

        if (!programExercise) {
          return reply.status(404).send({
            error: 'Program exercise not found',
          });
        }

        return reply.status(200).send(programExercise);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to get program exercise',
        });
      }
    }
  );

  /**
   * PATCH /api/program-exercises/:id
   *
   * Update sets/reps/RIR with volume warning
   *
   * Body: { target_sets?, target_rep_range?, target_rir? }
   * Response: { updated: true, volume_warning? }
   *
   * Requires JWT authentication
   */
  fastify.patch(
    '/program-exercises/:id',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const params = request.params as { id: string };
        const body = request.body as UpdateProgramExerciseRequest;
        const programExerciseId = parseInt(params.id, 10);

        // Validate user ownership
        if (!validateUserOwnership(programExerciseId, authenticatedUser.userId)) {
          return reply.status(404).send({
            error: 'Program exercise not found',
          });
        }

        // Validate target_sets if provided
        if (body.target_sets !== undefined && (body.target_sets < 1 || body.target_sets > 10)) {
          return reply.status(400).send({
            error: 'target_sets must be between 1 and 10',
          });
        }

        // Validate target_rep_range format if provided
        if (body.target_rep_range !== undefined && !/^\d+-\d+$/.test(body.target_rep_range)) {
          return reply.status(400).send({
            error: 'target_rep_range must be in format "N-M" (e.g., "8-12")',
          });
        }

        // Validate target_rir if provided
        if (body.target_rir !== undefined && (body.target_rir < 0 || body.target_rir > 4)) {
          return reply.status(400).send({
            error: 'target_rir must be between 0 and 4',
          });
        }

        // Call service
        const result = programExerciseService.updateProgramExercise(programExerciseId, body);

        return reply.status(200).send(result);
      } catch (error: any) {
        if (error.message?.includes('not found')) {
          return reply.status(404).send({
            error: error.message,
          });
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to update program exercise',
        });
      }
    }
  );

  /**
   * DELETE /api/program-exercises/:id
   *
   * Remove exercise with volume warning
   *
   * Response: { deleted: true, volume_warning? }
   *
   * Requires JWT authentication
   */
  fastify.delete(
    '/program-exercises/:id',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const params = request.params as { id: string };
        const programExerciseId = parseInt(params.id, 10);

        // Validate user ownership
        if (!validateUserOwnership(programExerciseId, authenticatedUser.userId)) {
          return reply.status(404).send({
            error: 'Program exercise not found',
          });
        }

        // Call service
        const result = programExerciseService.deleteProgramExercise(programExerciseId);

        return reply.status(200).send(result);
      } catch (error: any) {
        if (error.message?.includes('not found')) {
          return reply.status(404).send({
            error: error.message,
          });
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to delete program exercise',
        });
      }
    }
  );

  /**
   * PUT /api/program-exercises/:id/swap
   *
   * Swap exercise with compatible alternative (preserve order_index)
   *
   * Body: { new_exercise_id }
   * Response: { swapped: true, old_exercise_name, new_exercise_name, volume_warning? }
   *
   * Requires JWT authentication
   */
  fastify.put(
    '/program-exercises/:id/swap',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const params = request.params as { id: string };
        const body = request.body as SwapExerciseRequest;
        const programExerciseId = parseInt(params.id, 10);

        // Validate required fields
        if (!body.new_exercise_id) {
          return reply.status(400).send({
            error: 'Missing required field: new_exercise_id',
          });
        }

        // Validate user ownership
        if (!validateUserOwnership(programExerciseId, authenticatedUser.userId)) {
          return reply.status(404).send({
            error: 'Program exercise not found',
          });
        }

        // Call service (this will throw if incompatible or not found)
        const result = programExerciseService.swapExercise(programExerciseId, body.new_exercise_id);

        return reply.status(200).send(result);
      } catch (error: any) {
        if (error.message?.includes('not found')) {
          return reply.status(404).send({
            error: error.message,
          });
        }

        if (error.message?.includes('incompatible')) {
          return reply.status(400).send({
            error: error.message,
          });
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to swap exercise',
        });
      }
    }
  );

  /**
   * PATCH /api/program-exercises/batch-reorder
   *
   * Reorder multiple exercises atomically (drag-and-drop)
   *
   * Body: { program_day_id, exercise_order: [{ program_exercise_id, new_order_index }] }
   * Response: { reordered: true }
   *
   * Requires JWT authentication
   */
  fastify.patch(
    '/program-exercises/batch-reorder',
    { preHandler: authenticateJWT },
    async (request: FastifyRequest, reply: FastifyReply) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const body = request.body as BatchReorderRequest;

        // Validate required fields
        if (!body.program_day_id) {
          return reply.status(400).send({
            error: 'Missing required field: program_day_id',
          });
        }

        if (!body.exercise_order || !Array.isArray(body.exercise_order)) {
          return reply.status(400).send({
            error: 'Missing or invalid field: exercise_order (must be array)',
          });
        }

        // Validate user owns this program day
        if (!validateProgramDayOwnership(body.program_day_id, authenticatedUser.userId)) {
          return reply.status(404).send({
            error: 'Program day not found',
          });
        }

        // Validate exercise_order items
        for (const item of body.exercise_order) {
          if (!item.program_exercise_id || item.new_order_index === undefined) {
            return reply.status(400).send({
              error: 'Each exercise_order item must have program_exercise_id and new_order_index',
            });
          }

          if (item.new_order_index < 0) {
            return reply.status(400).send({
              error: 'new_order_index must be non-negative',
            });
          }

          // Validate user owns each program exercise
          if (!validateUserOwnership(item.program_exercise_id, authenticatedUser.userId)) {
            return reply.status(403).send({
              error: `Access denied to program exercise ${item.program_exercise_id}`,
            });
          }
        }

        // Call service
        const result = programExerciseService.reorderExercises(
          body.program_day_id,
          body.exercise_order
        );

        return reply.status(200).send(result);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to reorder exercises',
        });
      }
    }
  );
}

/**
 * VO2max Cardio Session Routes
 *
 * REST endpoints for cardio session tracking with Norwegian 4x4 and Zone 2 protocols:
 * - POST /api/vo2max-sessions - Create VO2max session with auto-calculation
 * - GET /api/vo2max-sessions - List sessions with filtering and pagination
 * - GET /api/vo2max-sessions/:id - Get single session details
 * - GET /api/vo2max-sessions/progression - Get VO2max progression over time
 * - PATCH /api/vo2max-sessions/:id - Update session
 */

import { FastifyInstance, FastifyRequest, FastifyReply } from 'fastify';
import {
  createVO2maxSession,
  getVO2maxSessions,
  getVO2maxSessionById,
  getVO2maxProgression,
  VO2maxSessionData,
  VO2maxSessionFilters,
} from '../services/vo2maxService.js';
import { authenticateJWT, AuthenticatedRequest } from '../middleware/auth.js';
import { db } from '../database/db.js';

/**
 * Create VO2max session request body interface
 */
interface CreateVO2maxSessionBody {
  workout_id?: number;
  date: string;
  duration_minutes: number;
  protocol_type: 'norwegian_4x4' | 'zone2';
  average_heart_rate?: number;
  peak_heart_rate?: number;
  estimated_vo2max?: number;
  intervals_completed?: number;
  rpe?: number;
  notes?: string;
}

/**
 * List VO2max sessions query parameters
 */
interface ListVO2maxSessionsQuery {
  start_date?: string;
  end_date?: string;
  protocol_type?: 'norwegian_4x4' | 'zone2';
  limit?: number;
  offset?: number;
}

/**
 * Progression query parameters
 */
interface ProgressionQuery {
  start_date?: string;
  end_date?: string;
}

/**
 * Update session body
 */
interface UpdateSessionBody {
  duration_minutes?: number;
  average_heart_rate?: number;
  peak_heart_rate?: number;
  estimated_vo2max?: number;
  intervals_completed?: number;
  rpe?: number;
  notes?: string;
  completion_status?: 'completed' | 'incomplete';
}

/**
 * Create VO2max session schema
 */
const createSessionSchema = {
  schema: {
    body: {
      type: 'object',
      required: ['date', 'duration_minutes', 'protocol_type'],
      properties: {
        workout_id: { type: 'integer' },
        date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
        },
        duration_minutes: {
          type: 'integer',
          minimum: 10,
          maximum: 120,
        },
        protocol_type: {
          type: 'string',
          enum: ['norwegian_4x4', 'zone2'],
        },
        average_heart_rate: {
          type: 'integer',
          minimum: 60,
          maximum: 220,
        },
        peak_heart_rate: {
          type: 'integer',
          minimum: 60,
          maximum: 220,
        },
        estimated_vo2max: {
          type: 'number',
          minimum: 20.0,
          maximum: 80.0,
        },
        intervals_completed: {
          type: 'integer',
          minimum: 0,
          maximum: 4,
        },
        rpe: {
          type: 'integer',
          minimum: 1,
          maximum: 10,
        },
        notes: {
          type: 'string',
          maxLength: 500,
        },
      },
    },
  },
};

/**
 * List sessions schema
 */
const listSessionsSchema = {
  schema: {
    querystring: {
      type: 'object',
      properties: {
        start_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
        },
        end_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
        },
        protocol_type: {
          type: 'string',
          enum: ['norwegian_4x4', 'zone2'],
        },
        limit: {
          type: 'integer',
          minimum: 1,
        },
        offset: {
          type: 'integer',
          minimum: 0,
        },
      },
    },
  },
};

/**
 * Progression schema
 */
const progressionSchema = {
  schema: {
    querystring: {
      type: 'object',
      properties: {
        start_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
        },
        end_date: {
          type: 'string',
          pattern: '^\\d{4}-\\d{2}-\\d{2}$',
        },
      },
    },
  },
};

/**
 * Update session schema
 */
const updateSessionSchema = {
  schema: {
    params: {
      type: 'object',
      required: ['id'],
      properties: {
        id: { type: 'string' },
      },
    },
    body: {
      type: 'object',
      minProperties: 1,
      properties: {
        duration_minutes: {
          type: 'integer',
          minimum: 10,
          maximum: 120,
        },
        average_heart_rate: {
          type: 'integer',
          minimum: 60,
          maximum: 220,
        },
        peak_heart_rate: {
          type: 'integer',
          minimum: 60,
          maximum: 220,
        },
        estimated_vo2max: {
          type: 'number',
          minimum: 20.0,
          maximum: 80.0,
        },
        intervals_completed: {
          type: 'integer',
          minimum: 0,
          maximum: 4,
        },
        rpe: {
          type: 'integer',
          minimum: 1,
          maximum: 10,
        },
        notes: {
          type: 'string',
          maxLength: 500,
        },
        completion_status: {
          type: 'string',
          enum: ['completed', 'incomplete'],
        },
      },
    },
  },
};

/**
 * Transform database row to API response format
 */
function transformSessionToResponse(row: any): any {
  return {
    id: row.id,
    user_id: row.user_id,
    workout_id: row.workout_id,
    date: row.date,
    duration_minutes: Math.floor(row.duration_seconds / 60),
    protocol_type: row.protocol === '4x4' ? 'norwegian_4x4' : 'zone2',
    average_heart_rate: row.average_hr,
    peak_heart_rate: row.peak_hr,
    estimated_vo2max: row.estimated_vo2max,
    intervals_completed: row.intervals_completed,
    rpe: row.rpe,
    completion_status: row.completion_status || 'incomplete',
    notes: row.notes,
    created_at: row.created_at,
  };
}

/**
 * Register VO2max routes
 */
export default async function vo2maxRoutes(fastify: FastifyInstance) {
  /**
   * POST /api/vo2max-sessions
   *
   * Create a new VO2max cardio session
   * Auto-calculates estimated_vo2max using Cooper formula if not provided
   *
   * Requires JWT authentication
   */
  fastify.post<{ Body: CreateVO2maxSessionBody }>(
    '/vo2max-sessions',
    {
      ...createSessionSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Body: CreateVO2maxSessionBody }>,
      reply: FastifyReply
    ) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const {
          workout_id,
          date,
          duration_minutes,
          protocol_type,
          average_heart_rate,
          peak_heart_rate,
          estimated_vo2max,
          intervals_completed,
          rpe,
          notes,
        } = request.body;

        // Create or get workout for this session
        let workoutId = workout_id;

        if (!workoutId) {
          // Create a placeholder workout for this cardio session
          // Find or create a VO2max program day
          const vo2maxDay = db
            .prepare(
              `
              SELECT pd.id
              FROM program_days pd
              JOIN programs p ON pd.program_id = p.id
              WHERE p.user_id = ? AND pd.day_type = 'vo2max'
              LIMIT 1
            `
            )
            .get(authenticatedUser.userId) as { id: number } | undefined;

          if (!vo2maxDay) {
            return reply.status(400).send({
              error:
                'No VO2max program day found. Please create a program with VO2max days first.',
            });
          }

          // Create workout
          const workoutResult = db
            .prepare(
              `
              INSERT INTO workouts (user_id, program_day_id, date, status, synced)
              VALUES (?, ?, ?, 'completed', 1)
            `
            )
            .run(authenticatedUser.userId, vo2maxDay.id, date);

          workoutId = workoutResult.lastInsertRowid as number;
        } else {
          // Verify workout ownership
          const workout = db
            .prepare('SELECT user_id FROM workouts WHERE id = ?')
            .get(workoutId) as { user_id: number } | undefined;

          if (!workout || workout.user_id !== authenticatedUser.userId) {
            return reply.status(403).send({
              error: 'Not authorized to access this workout',
            });
          }
        }

        // Determine completion status
        let completionStatus: 'completed' | 'incomplete' = 'incomplete';
        if (protocol_type === 'norwegian_4x4') {
          // Norwegian 4x4 is complete if all 4 intervals done
          if (intervals_completed === 4) {
            completionStatus = 'completed';
          }
        } else {
          // Zone 2 is complete if duration >= 45 minutes
          if (duration_minutes >= 45) {
            completionStatus = 'completed';
          }
        }

        // Create session data
        const sessionData: VO2maxSessionData = {
          workout_id: workoutId,
          user_id: authenticatedUser.userId,
          protocol_type,
          duration_minutes,
          intervals_completed,
          average_heart_rate,
          peak_heart_rate,
          estimated_vo2max,
        };

        // Create VO2max session (service handles auto-calculation)
        const sessionId = createVO2maxSession(sessionData);

        // Update additional fields (rpe, notes, completion_status, created_at)
        db.prepare(
          `
          UPDATE vo2max_sessions
          SET rpe = ?, notes = ?, completion_status = ?, created_at = ?
          WHERE id = ?
        `
        ).run(rpe ?? null, notes ?? null, completionStatus, Date.now(), sessionId);

        // Fetch the created session
        const createdSession = db
          .prepare(
            `
            SELECT v.*, w.date, w.user_id
            FROM vo2max_sessions v
            JOIN workouts w ON v.workout_id = w.id
            WHERE v.id = ?
          `
          )
          .get(sessionId);

        const response = transformSessionToResponse(createdSession);

        return reply.status(201).send({
          session_id: sessionId,
          estimated_vo2max: response.estimated_vo2max,
          completion_status: response.completion_status,
        });
      } catch (error) {
        // Handle validation errors from service or database triggers
        if (error instanceof Error) {
          if (
            error.message.includes('must be between') ||
            error.message.includes('Invalid') ||
            error.message.includes('ABORT')
          ) {
            return reply.status(400).send({
              error: error.message,
            });
          }
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to create VO2max session',
        });
      }
    }
  );

  /**
   * GET /api/vo2max-sessions
   *
   * List VO2max sessions for the authenticated user
   * Supports filtering by date range, protocol type, and pagination
   *
   * Requires JWT authentication
   */
  fastify.get<{ Querystring: ListVO2maxSessionsQuery }>(
    '/vo2max-sessions',
    {
      ...listSessionsSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Querystring: ListVO2maxSessionsQuery }>,
      reply: FastifyReply
    ) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const { start_date, end_date, protocol_type, limit = 50, offset = 0 } = request.query;

        const filters: VO2maxSessionFilters = {
          user_id: authenticatedUser.userId,
          start_date,
          end_date,
          protocol_type,
          limit: Math.min(limit, 200), // Enforce max limit
          offset,
        };

        const sessions = getVO2maxSessions(filters);
        const transformedSessions = sessions.map(transformSessionToResponse);

        // Get total count for has_more flag
        const totalCount = db
          .prepare(
            `
            SELECT COUNT(*) as count
            FROM vo2max_sessions v
            JOIN workouts w ON v.workout_id = w.id
            WHERE w.user_id = ?
          `
          )
          .get(authenticatedUser.userId) as { count: number };

        const hasMore = offset + transformedSessions.length < totalCount.count;

        return reply.status(200).send({
          sessions: transformedSessions,
          count: transformedSessions.length,
          has_more: hasMore,
        });
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to list VO2max sessions',
        });
      }
    }
  );

  /**
   * GET /api/vo2max-sessions/progression
   *
   * Get VO2max progression over time
   * Returns sessions with estimated VO2max ordered by date (oldest first)
   *
   * Requires JWT authentication
   */
  fastify.get<{ Querystring: ProgressionQuery }>(
    '/vo2max-sessions/progression',
    {
      ...progressionSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Querystring: ProgressionQuery }>,
      reply: FastifyReply
    ) => {
      try {
        const authenticatedUser = (request as AuthenticatedRequest).user;
        const { start_date, end_date } = request.query;

        const progression = getVO2maxProgression(
          authenticatedUser.userId,
          start_date,
          end_date
        );

        // Transform protocol format
        const transformedProgression = progression.map((point) => ({
          date: point.date,
          estimated_vo2max: point.estimated_vo2max,
          protocol_type: point.protocol === '4x4' ? 'norwegian_4x4' : 'zone2',
        }));

        return reply.status(200).send({
          sessions: transformedProgression,
        });
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to get VO2max progression',
        });
      }
    }
  );

  /**
   * GET /api/vo2max-sessions/:id
   *
   * Get a single VO2max session by ID
   * Verifies user ownership
   *
   * Requires JWT authentication
   */
  fastify.get<{ Params: { id: string } }>(
    '/vo2max-sessions/:id',
    {
      preHandler: authenticateJWT,
      schema: {
        params: {
          type: 'object',
          required: ['id'],
          properties: {
            id: { type: 'string' },
          },
        },
      },
    },
    async (request: FastifyRequest<{ Params: { id: string } }>, reply: FastifyReply) => {
      try {
        const sessionId = parseInt(request.params.id, 10);
        const authenticatedUser = (request as AuthenticatedRequest).user;

        const session = getVO2maxSessionById(sessionId, authenticatedUser.userId);

        if (!session) {
          return reply.status(404).send({
            error: 'VO2max session not found',
          });
        }

        const response = transformSessionToResponse(session);

        return reply.status(200).send(response);
      } catch (error) {
        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to get VO2max session',
        });
      }
    }
  );

  /**
   * PATCH /api/vo2max-sessions/:id
   *
   * Update a VO2max session
   * Verifies user ownership
   *
   * Requires JWT authentication
   */
  fastify.patch<{ Params: { id: string }; Body: UpdateSessionBody }>(
    '/vo2max-sessions/:id',
    {
      ...updateSessionSchema,
      preHandler: authenticateJWT,
    },
    async (
      request: FastifyRequest<{ Params: { id: string }; Body: UpdateSessionBody }>,
      reply: FastifyReply
    ) => {
      try {
        const sessionId = parseInt(request.params.id, 10);
        const authenticatedUser = (request as AuthenticatedRequest).user;

        // Verify ownership
        const session = getVO2maxSessionById(sessionId, authenticatedUser.userId);

        if (!session) {
          return reply.status(404).send({
            error: 'VO2max session not found',
          });
        }

        const {
          duration_minutes,
          average_heart_rate,
          peak_heart_rate,
          estimated_vo2max,
          intervals_completed,
          rpe,
          notes,
          completion_status,
        } = request.body;

        // Build dynamic update query
        const updates: string[] = [];
        const params: any[] = [];

        if (duration_minutes !== undefined) {
          updates.push('duration_seconds = ?');
          params.push(duration_minutes * 60);
        }

        if (average_heart_rate !== undefined) {
          updates.push('average_hr = ?');
          params.push(average_heart_rate);
        }

        if (peak_heart_rate !== undefined) {
          updates.push('peak_hr = ?');
          params.push(peak_heart_rate);
        }

        if (estimated_vo2max !== undefined) {
          updates.push('estimated_vo2max = ?');
          params.push(estimated_vo2max);
        }

        if (intervals_completed !== undefined) {
          updates.push('intervals_completed = ?');
          params.push(intervals_completed);
        }

        if (rpe !== undefined) {
          updates.push('rpe = ?');
          params.push(rpe);
        }

        if (notes !== undefined) {
          updates.push('notes = ?');
          params.push(notes);
        }

        if (completion_status !== undefined) {
          updates.push('completion_status = ?');
          params.push(completion_status);
        }

        if (updates.length === 0) {
          return reply.status(400).send({
            error: 'No fields to update',
          });
        }

        params.push(sessionId);

        const query = `
          UPDATE vo2max_sessions
          SET ${updates.join(', ')}
          WHERE id = ?
        `;

        db.prepare(query).run(...params);

        // Fetch updated session
        const updatedSession = getVO2maxSessionById(sessionId, authenticatedUser.userId);
        const response = transformSessionToResponse(updatedSession);

        return reply.status(200).send(response);
      } catch (error) {
        // Handle validation errors from database triggers
        if (error instanceof Error) {
          if (error.message.includes('must be between') || error.message.includes('ABORT')) {
            return reply.status(400).send({
              error: error.message,
            });
          }
        }

        fastify.log.error(error);
        return reply.status(500).send({
          error: 'Failed to update VO2max session',
        });
      }
    }
  );
}

/**
 * Authentication Service
 *
 * Implements user registration and login with bcrypt password hashing
 * and JWT token generation (30-day expiration per justified constitutional violation)
 */

import bcrypt from 'bcrypt';
import {
  stmtGetUserByUsername,
  stmtCreateUser,
} from '../database/db.js';
import { createDefaultProgram } from './programService.js';

// Bcrypt cost factor (constitutional requirement)
const BCRYPT_COST = 12;

// JWT expiration (30 days - justified violation for home server use case)
export const JWT_EXPIRATION = '30d';

/**
 * User interface (excluding password_hash for security)
 */
export interface User {
  id: number;
  username: string;
  age?: number;
  weight_kg?: number;
  experience_level?: 'beginner' | 'intermediate' | 'advanced';
  created_at: number;
  updated_at: number;
}

/**
 * Registration response
 */
export interface RegisterResponse {
  user_id: number;
  userId: number; // Alias for compatibility
  username: string;
  token: string;
}

/**
 * Login response
 */
export interface LoginResponse {
  token: string;
  user: User;
}

/**
 * Register a new user
 *
 * @param username - User's email address (used as username)
 * @param password - Plain text password (will be hashed with bcrypt cost=12)
 * @param age - Optional user age (13-100)
 * @param weight_kg - Optional user weight in kg (30-300)
 * @param experience_level - Optional experience level
 * @param jwtSign - Fastify JWT sign function
 * @returns Object containing user_id and JWT token
 * @throws Error if username already exists or database error occurs
 */
export async function registerUser(
  username: string,
  password: string,
  age: number | undefined,
  weight_kg: number | undefined,
  experience_level: 'beginner' | 'intermediate' | 'advanced' | undefined,
  jwtSign: (payload: { userId: number; username: string }) => string
): Promise<RegisterResponse> {
  // Check if username already exists
  const existingUser = stmtGetUserByUsername.get(username) as User | undefined;
  if (existingUser) {
    throw new Error('Username already exists');
  }

  // Hash password with bcrypt (cost=12)
  const password_hash = await bcrypt.hash(password, BCRYPT_COST);

  // Insert user into database
  const now = Date.now();
  const result = stmtCreateUser.run(
    username,
    password_hash,
    age ?? null,
    weight_kg ?? null,
    experience_level ?? null,
    now,
    now
  );

  const user_id = result.lastInsertRowid as number;

  // Create default 6-day Renaissance Periodization program for new user
  try {
    createDefaultProgram(user_id);
  } catch (error) {
    // Log error but don't fail registration - user can still use the app
    console.error(`Failed to create default program for user ${user_id}:`, error);
  }

  // Generate JWT token with 30-day expiration
  const token = jwtSign({
    userId: user_id,
    username,
  });

  return {
    user_id,
    userId: user_id, // Alias for compatibility
    username,
    token,
  };
}

/**
 * Login existing user
 *
 * @param username - User's email address
 * @param password - Plain text password
 * @param jwtSign - Fastify JWT sign function
 * @returns Object containing JWT token and user data
 * @throws Error if credentials are invalid
 */
export async function loginUser(
  username: string,
  password: string,
  jwtSign: (payload: { userId: number; username: string }) => string
): Promise<LoginResponse> {
  // Get user by username
  const user = stmtGetUserByUsername.get(username) as
    | (User & { password_hash: string })
    | undefined;

  if (!user) {
    throw new Error('Invalid credentials');
  }

  // Verify password with bcrypt
  const isValidPassword = await bcrypt.compare(password, user.password_hash);
  if (!isValidPassword) {
    throw new Error('Invalid credentials');
  }

  // Generate JWT token
  const token = jwtSign({
    userId: user.id,
    username: user.username,
  });

  // Return user data (excluding password_hash)
  const { password_hash, ...userData } = user;

  return {
    token,
    user: userData,
  };
}

/**
 * JWT Authentication Middleware
 *
 * Verifies JWT tokens from Authorization header and attaches user info to request
 */

import { FastifyRequest, FastifyReply } from 'fastify';

/**
 * Extended FastifyRequest with authenticated user information
 */
export interface AuthenticatedRequest extends FastifyRequest {
  user: {
    userId: number;
    username: string;
  };
}

/**
 * JWT Authentication Middleware
 *
 * Verifies JWT from Authorization header (Bearer token format)
 * Attaches decoded user info to req.user for authenticated routes
 * Returns 401 if token is invalid, expired, or missing
 *
 * Usage:
 * fastify.addHook('preHandler', authenticateJWT)
 * OR
 * fastify.get('/protected', { preHandler: authenticateJWT }, handler)
 */
export async function authenticateJWT(
  request: FastifyRequest,
  reply: FastifyReply
): Promise<void> {
  try {
    // Verify JWT token - Fastify JWT plugin automatically checks Authorization header
    await request.jwtVerify();

    // Token is valid, user payload is attached by @fastify/jwt
    // The plugin attaches the decoded payload to request.user
    // Type assertion needed because Fastify types don't know our payload structure
  } catch (error) {
    // Token is invalid, expired, or missing
    return reply.status(401).send({
      error: 'Unauthorized - Invalid or expired token',
    });
  }
}

/**
 * Backend Database Initialization
 *
 * Initializes better-sqlite3 with performance optimizations:
 * - WAL mode for concurrent reads
 * - 64MB cache for query performance
 * - 256MB memory-mapped I/O
 * - Prepared statements for common queries
 */

import Database, { Statement } from 'better-sqlite3';
import { readFileSync, mkdirSync, existsSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';

// Get the directory of this module file
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

// Database file path - from dist/database to backend root
const DB_PATH = join(__dirname, '../../data/fitflow.db');
const SCHEMA_PATH = join(__dirname, '../../src/database/schema.sql');

// Ensure data directory exists
const dataDir = dirname(DB_PATH);
if (!existsSync(dataDir)) {
  mkdirSync(dataDir, { recursive: true });
}

// Initialize database
export const db: Database.Database = new Database(DB_PATH, {
  verbose: process.env.NODE_ENV === 'development' ? console.log : undefined,
});

// Apply performance optimizations
db.pragma('journal_mode = WAL');
db.pragma('cache_size = -64000'); // 64MB cache
db.pragma('mmap_size = 268435456'); // 256MB memory-mapped I/O
db.pragma('foreign_keys = ON');

// Initialize schema on first run
const initializeSchema = (): void => {
  const schema = readFileSync(SCHEMA_PATH, 'utf-8');
  db.exec(schema);
  console.log('Database schema initialized');
};

// Check if database is initialized (check for users table)
const isInitialized = (): boolean => {
  try {
    const result = db
      .prepare(
        "SELECT name FROM sqlite_master WHERE type='table' AND name='users'"
      )
      .get();
    return !!result;
  } catch {
    return false;
  }
};

// Initialize if needed
if (!isInitialized()) {
  initializeSchema();
}

