    query += ' AND pe.exercise_id = ?';
    params.push(filters.exercise_id);
  }

  query += ' ORDER BY pe.order_index';

  const stmt = db.prepare(query);
  return stmt.all(...params);
}

/**
 * Create a new program exercise with volume warning
 *
 * @param data - Program exercise data
 * @returns Object with program_exercise_id and optional volume_warning
 * @throws Error if program_day_id or exercise_id doesn't exist
 */
export function createProgramExercise(data: CreateProgramExerciseData): {
  program_exercise_id: number;
  volume_warning: string | null;
} {
  // Validate program_day_id exists
  const programDayStmt = db.prepare('SELECT id FROM program_days WHERE id = ?');
  const programDay = programDayStmt.get(data.program_day_id);
  if (!programDay) {
    throw new Error(`Program day with ID ${data.program_day_id} not found`);
  }

  // Validate exercise_id exists
  const exerciseStmt = db.prepare('SELECT * FROM exercises WHERE id = ?');
  const exercise = exerciseStmt.get(data.exercise_id) as any;
  if (!exercise) {
    throw new Error(`Exercise with ID ${data.exercise_id} not found`);
  }

  // Determine order_index if not provided
  let orderIndex = data.order_index;
  if (orderIndex === undefined) {
    const maxOrderStmt = db.prepare(
      'SELECT MAX(order_index) as max_order FROM program_exercises WHERE program_day_id = ?'
    );
    const result = maxOrderStmt.get(data.program_day_id) as any;
    orderIndex = (result?.max_order ?? 0) + 1;
  }

  // Insert program exercise
  const insertStmt = db.prepare(`
    INSERT INTO program_exercises (program_day_id, exercise_id, order_index, sets, reps, rir)
    VALUES (?, ?, ?, ?, ?, ?)
  `);

  const insertResult = insertStmt.run(
    data.program_day_id,
    data.exercise_id,
    orderIndex,
    data.target_sets,
    data.target_rep_range,
    data.target_rir
  );

  const programExerciseId = insertResult.lastInsertRowid as number;

  // Calculate volume warning
  const volumeWarning = calculateVolumeWarning(data.program_day_id, 'add', data.target_sets, exercise);

  return {
    program_exercise_id: programExerciseId,
    volume_warning: volumeWarning,
  };
}

/**
 * Update a program exercise
 *
 * @param id - Program exercise ID
 * @param data - Update data (target_sets, target_rep_range, target_rir)
 * @returns Object with updated=true and optional volume_warning
 * @throws Error if program exercise doesn't exist
 */
export function updateProgramExercise(
  id: number,
  data: UpdateProgramExerciseData
): { updated: boolean; volume_warning: string | null } {
  // Check if program exercise exists
  const checkStmt = db.prepare('SELECT * FROM program_exercises WHERE id = ?');
  const programExercise = checkStmt.get(id) as any;
  if (!programExercise) {
    throw new Error(`Program exercise with ID ${id} not found`);
  }

  // Build dynamic update query
  const updates: string[] = [];
  const params: any[] = [];

  if (data.target_sets !== undefined) {
    updates.push('sets = ?');
    params.push(data.target_sets);
  }

  if (data.target_rep_range !== undefined) {
    updates.push('reps = ?');
    params.push(data.target_rep_range);
  }

  if (data.target_rir !== undefined) {
    updates.push('rir = ?');
    params.push(data.target_rir);
  }

  if (updates.length === 0) {
    // No updates to perform
    return { updated: false, volume_warning: null };
  }

  params.push(id);

  const updateStmt = db.prepare(`
    UPDATE program_exercises
    SET ${updates.join(', ')}
    WHERE id = ?
  `);

  updateStmt.run(...params);

  // Calculate volume warning if sets changed
  let volumeWarning: string | null = null;
  if (data.target_sets !== undefined) {
    const exerciseStmt = db.prepare(`
      SELECT e.* FROM exercises e
      JOIN program_exercises pe ON e.id = pe.exercise_id
      WHERE pe.id = ?
    `);
    const exercise = exerciseStmt.get(id) as any;

    volumeWarning = calculateVolumeWarning(
      programExercise.program_day_id,
      'update',
      data.target_sets,
      exercise,
      programExercise.sets
    );
  }

  return { updated: true, volume_warning: volumeWarning };
}

/**
 * Delete a program exercise
 *
 * @param id - Program exercise ID
 * @returns Object with deleted=true and optional volume_warning
 * @throws Error if program exercise doesn't exist
 */
export function deleteProgramExercise(id: number): {
  deleted: boolean;
  volume_warning: string | null;
} {
  // Get program exercise details before deletion
  const stmt = db.prepare(`
    SELECT pe.*, e.muscle_groups
    FROM program_exercises pe
    JOIN exercises e ON pe.exercise_id = e.id
    WHERE pe.id = ?
  `);
  const programExercise = stmt.get(id) as any;

  if (!programExercise) {
    throw new Error(`Program exercise with ID ${id} not found`);
  }

  // Calculate volume warning before deletion
  const volumeWarning = calculateVolumeWarning(
    programExercise.program_day_id,
    'delete',
    programExercise.sets,
    programExercise
  );

  // Delete the program exercise
  const deleteStmt = db.prepare('DELETE FROM program_exercises WHERE id = ?');
  deleteStmt.run(id);

  return {
    deleted: true,
    volume_warning: volumeWarning,
  };
}

/**
 * Swap an exercise with a compatible alternative
 *
 * @param programExerciseId - Program exercise ID to swap
 * @param newExerciseId - New exercise ID
 * @returns Object with swapped=true, old_exercise_name, new_exercise_name
 * @throws Error if exercises are incompatible or don't exist
 */
export function swapExercise(programExerciseId: number, newExerciseId: number): {
  swapped: boolean;
  old_exercise_name: string;
  new_exercise_name: string;
} {
  // Get current program exercise
  const programExerciseStmt = db.prepare(`
    SELECT pe.*, e.name as exercise_name, e.muscle_groups
    FROM program_exercises pe
    JOIN exercises e ON pe.exercise_id = e.id
    WHERE pe.id = ?
  `);
  const programExercise = programExerciseStmt.get(programExerciseId) as any;

  if (!programExercise) {
    throw new Error(`Program exercise with ID ${programExerciseId} not found`);
  }

  // Get new exercise
  const newExerciseStmt = db.prepare('SELECT * FROM exercises WHERE id = ?');
  const newExercise = newExerciseStmt.get(newExerciseId) as any;

  if (!newExercise) {
    throw new Error(`Exercise with ID ${newExerciseId} not found`);
  }

  // Check compatibility (at least one shared muscle group)
  const oldMuscleGroups = JSON.parse(programExercise.muscle_groups) as string[];
  const newMuscleGroups = JSON.parse(newExercise.muscle_groups) as string[];

  const hasSharedMuscle = oldMuscleGroups.some((mg) => newMuscleGroups.includes(mg));

  if (!hasSharedMuscle) {
    throw new Error(
      `Exercise "${newExercise.name}" is incompatible with "${programExercise.exercise_name}". ` +
        `Old targets: [${oldMuscleGroups.join(', ')}], New targets: [${newMuscleGroups.join(', ')}]`
    );
  }

  // Perform swap (preserve order_index, sets, reps, rir)
  const updateStmt = db.prepare('UPDATE program_exercises SET exercise_id = ? WHERE id = ?');
  updateStmt.run(newExerciseId, programExerciseId);

  return {
    swapped: true,
    old_exercise_name: programExercise.exercise_name,
    new_exercise_name: newExercise.name,
  };
}

/**
 * Reorder exercises within a program day
 *
 * @param programDayId - Program day ID
 * @param newOrder - Array of {program_exercise_id, new_order_index}
 * @returns Object with reordered=true
 */
export function reorderExercises(
  programDayId: number,
  newOrder: ExerciseReorderItem[]
): { reordered: boolean } {
  // Execute as transaction for atomicity
  const reorder = db.transaction(() => {
    const updateStmt = db.prepare('UPDATE program_exercises SET order_index = ? WHERE id = ?');

    for (const item of newOrder) {
      updateStmt.run(item.new_order_index, item.program_exercise_id);
    }
  });

  reorder();

  return { reordered: true };
}

/**
 * Calculate volume warning when adding/updating/deleting exercises
 *
 * @param programDayId - Program day ID
 * @param operation - 'add', 'update', or 'delete'
 * @param sets - Number of sets being added/removed
 * @param exercise - Exercise data with muscle_groups
 * @param oldSets - Previous sets count (for update operation)
 * @returns Volume warning message or null
 */
function calculateVolumeWarning(
  programDayId: number,
  operation: 'add' | 'update' | 'delete',
  sets: number,
  exercise: any,
  oldSets?: number
): string | null {
  // Get program_id from program_day_id
  const programStmt = db.prepare(`
    SELECT program_id FROM program_days WHERE id = ?
  `);
  const programDay = programStmt.get(programDayId) as any;
  if (!programDay) {
    return null;
  }

  const programId = programDay.program_id;

  // Get all program days for this program
  const programDayIdsStmt = db.prepare(`
    SELECT id FROM program_days WHERE program_id = ?
  `);
  const programDayIds = (programDayIdsStmt.all(programId) as any[]).map((row) => row.id);

  // Get all exercises across all program days
  const exercisesStmt = db.prepare(`
    SELECT pe.sets, e.muscle_groups
    FROM program_exercises pe
    JOIN exercises e ON pe.exercise_id = e.id
    WHERE pe.program_day_id IN (${programDayIds.map(() => '?').join(',')})
  `);
  const allExercises = exercisesStmt.all(...programDayIds) as any[];

  // Parse muscle groups from exercise being added/updated/deleted
  const muscleGroups = JSON.parse(exercise.muscle_groups) as string[];

  // Calculate current weekly volume per muscle group
  const muscleVolume: Record<string, number> = {};

  for (const ex of allExercises) {
    const exMuscleGroups = JSON.parse(ex.muscle_groups) as string[];
    for (const mg of exMuscleGroups) {
      muscleVolume[mg] = (muscleVolume[mg] || 0) + ex.sets;
    }
  }

  // Apply operation delta
  let deltaMultiplier = 1;
  if (operation === 'add') {
    deltaMultiplier = 1; // Add sets
  } else if (operation === 'delete') {
    deltaMultiplier = -1; // Subtract sets
  } else if (operation === 'update' && oldSets !== undefined) {
    // Calculate net change
    const netChange = sets - oldSets;
    for (const mg of muscleGroups) {
      muscleVolume[mg] = (muscleVolume[mg] || 0) + netChange;
    }
    return checkVolumeThresholds(muscleVolume, muscleGroups, sets, operation);
  }

  // Apply delta for add/delete
  for (const mg of muscleGroups) {
    muscleVolume[mg] = (muscleVolume[mg] || 0) + sets * deltaMultiplier;
  }

  return checkVolumeThresholds(muscleVolume, muscleGroups, sets, operation);
}

/**
 * Check volume against MEV/MAV/MRV thresholds
 *
 * @param muscleVolume - Current weekly volume per muscle group
 * @param muscleGroups - Muscle groups affected by operation
 * @param sets - Sets being added/removed
 * @param operation - 'add', 'update', or 'delete'
 * @returns Warning message or null
 */
function checkVolumeThresholds(
  muscleVolume: Record<string, number>,
  muscleGroups: string[],
  sets: number,
  operation: 'add' | 'update' | 'delete'
): string | null {
  const warnings: string[] = [];

  for (const mg of muscleGroups) {
    const currentVolume = muscleVolume[mg] || 0;
    const landmarks = VOLUME_LANDMARKS[mg];

    if (!landmarks) {
      continue; // Skip if no landmarks defined for this muscle group
    }

    if (operation === 'add' || operation === 'update') {
      // Check if exceeding MRV
      if (currentVolume > landmarks.mrv) {
        warnings.push(
          `Adding this exercise will exceed MRV for ${mg} (${currentVolume} > ${landmarks.mrv})`
        );
      }
    } else if (operation === 'delete') {
      // Check if dropping below MEV
      if (currentVolume < landmarks.mev) {
        warnings.push(
          `Removing this exercise will drop below MEV for ${mg} (${currentVolume} < ${landmarks.mev})`
        );
      }
    }
  }

  return warnings.length > 0 ? warnings.join('; ') : null;
}

/**
 * VO2max Service
 *
 * Business logic for cardio session tracking with Norwegian 4x4 protocol:
 * - Creating VO2max sessions with Cooper formula estimation
 * - Filtering sessions by date range and protocol
 * - Tracking VO2max progression over time
 */

import { db } from '../database/db.js';

/**
 * VO2max session interface matching database schema
 */
export interface VO2maxSession {
  id: number;
  workout_id: number;
  protocol: '4x4' | 'zone2';
  duration_seconds: number;
  intervals_completed: number | null;
  average_hr: number | null;
  peak_hr: number | null;
  estimated_vo2max: number | null;
  synced: number;
}

/**
 * VO2max session creation data
 */
export interface VO2maxSessionData {
  workout_id: number;
  user_id: number; // For age lookup in Cooper formula
  protocol_type: 'norwegian_4x4' | 'zone2';
  duration_minutes: number;
  intervals_completed?: number;
  average_heart_rate?: number;
  peak_heart_rate?: number;
  estimated_vo2max?: number; // Optional - will be calculated if not provided
}

/**
 * VO2max session filter options
 */
export interface VO2maxSessionFilters {
  user_id: number;
  start_date?: string;
  end_date?: string;
  protocol_type?: 'norwegian_4x4' | 'zone2';
  limit?: number;
  offset?: number;
}

/**
 * VO2max progression data point
 */
export interface VO2maxProgressionPoint {
  date: string;
  estimated_vo2max: number;
  protocol: string;
}

/**
 * Estimate VO2max using Cooper Formula
 *
 * Cooper Formula: VO2max = 15.3  (max_hr / resting_hr)
 *
 * This is a simplified estimation that uses maximum heart rate (220 - age)
 * and assumes a standard resting heart rate of 60 bpm.
 *
 * @param age - User's age in years
 * @param averageHR - Average heart rate during session (optional, not used in Cooper formula)
 * @returns Estimated VO2max in ml/kg/min, clamped to physiological range 20-80
 */
export function estimateVO2max(age: number, _averageHR?: number): number {
  const maxHR = 220 - age;
  const restingHR = 60; // Standard assumption

  // Cooper Formula
  const vo2max = 15.3 * (maxHR / restingHR);

  // Clamp to physiological range: 20.0 - 80.0 ml/kg/min
  return Math.max(20.0, Math.min(80.0, vo2max));
}

/**
 * Validate VO2max session input data
 *
 * Enforces physiological constraints per Migration 003:
 * - Duration: 10-120 minutes (600-7200 seconds)
 * - Heart rate: 60-220 bpm
 * - VO2max: 20-80 ml/kg/min
 * - Norwegian 4x4: 4 intervals max
 *
 * @param data - Session data to validate
 * @throws Error if validation fails
 */
function validateSessionData(data: VO2maxSessionData): void {
  // Validate duration (10-120 minutes)
  if (data.duration_minutes < 10 || data.duration_minutes > 120) {
    throw new Error('Duration must be between 10 and 120 minutes');
  }

  // Validate average heart rate if provided
  if (data.average_heart_rate !== undefined && data.average_heart_rate !== null) {
    if (data.average_heart_rate < 60 || data.average_heart_rate > 220) {
      throw new Error('Average heart rate must be between 60 and 220 bpm');
    }
  }

  // Validate peak heart rate if provided
  if (data.peak_heart_rate !== undefined && data.peak_heart_rate !== null) {
    if (data.peak_heart_rate < 60 || data.peak_heart_rate > 220) {
      throw new Error('Peak heart rate must be between 60 and 220 bpm');
    }
  }

  // Validate estimated VO2max if provided
  if (data.estimated_vo2max !== undefined && data.estimated_vo2max !== null) {
    if (data.estimated_vo2max < 20.0 || data.estimated_vo2max > 80.0) {
      throw new Error('Estimated VO2max must be between 20.0 and 80.0 ml/kg/min');
    }
  }

  // Validate Norwegian 4x4 intervals
  if (data.protocol_type === 'norwegian_4x4') {
    if (data.intervals_completed !== undefined && data.intervals_completed !== null) {
      if (data.intervals_completed < 0 || data.intervals_completed > 4) {
        throw new Error('Norwegian 4x4 protocol allows 0-4 intervals');
      }
    }

    // Norwegian 4x4 should be 28-32 minutes (4  [4min work + 3min rest])
    // Allow some flexibility but warn if duration is unusual
    if (data.duration_minutes < 20 || data.duration_minutes > 40) {
      console.warn(
        `[VO2max] Norwegian 4x4 duration ${data.duration_minutes} minutes is outside typical range (28-32 minutes)`
      );
    }
  } else if (data.protocol_type === 'zone2') {
    // Zone 2 should be continuous, 45-120 minutes
    if (data.duration_minutes < 30) {
      console.warn(
        `[VO2max] Zone 2 duration ${data.duration_minutes} minutes is shorter than typical range (45-120 minutes)`
      );
    }
  }
}

/**
 * Create a new VO2max session with automatic VO2max estimation
 *
 * If estimated_vo2max is not provided and average_heart_rate is available,
 * the service will auto-calculate estimated_vo2max using the Cooper formula.
 *
 * @param data - VO2max session creation data
 * @returns The created VO2max session ID
 */
export function createVO2maxSession(data: VO2maxSessionData): number {
  // Validate input data
  validateSessionData(data);

  // Convert protocol type to database format
  const protocol: '4x4' | 'zone2' = data.protocol_type === 'norwegian_4x4' ? '4x4' : 'zone2';

  // Convert duration from minutes to seconds
  const durationSeconds = data.duration_minutes * 60;

  // Auto-calculate estimated_vo2max if not provided and HR available
  let estimatedVO2max = data.estimated_vo2max;

  if (estimatedVO2max === undefined && data.average_heart_rate !== undefined) {
    // Get user's age for Cooper formula
    const user = db
      .prepare('SELECT id, age FROM users WHERE id = ?')
      .get(data.user_id) as { id: number; age: number } | undefined;

    if (user && user.age) {
      estimatedVO2max = estimateVO2max(user.age, data.average_heart_rate);
      console.log(
        `[VO2max] Auto-calculated VO2max: ${estimatedVO2max.toFixed(1)} ml/kg/min ` +
        `(age=${user.age}, HR=${data.average_heart_rate})`
      );
    } else {
      console.warn(`[VO2max] Cannot calculate VO2max: user age not available`);
    }
  }

  // Insert VO2max session
  const result = db
    .prepare(
      `INSERT INTO vo2max_sessions (
        workout_id, protocol, duration_seconds, intervals_completed,
        average_hr, peak_hr, estimated_vo2max, synced, created_at
      )
      VALUES (?, ?, ?, ?, ?, ?, ?, 1, ?)`
    )
    .run(
      data.workout_id,
      protocol,
      durationSeconds,
      data.intervals_completed ?? null,
      data.average_heart_rate ?? null,
      data.peak_heart_rate ?? null,
      estimatedVO2max ?? null,
      Date.now()
    );

  const sessionId = result.lastInsertRowid as number;

  console.log(
    `[VO2max] Session created: id=${sessionId}, workout=${data.workout_id}, ` +
    `protocol=${protocol}, duration=${data.duration_minutes}min, VO2max=${estimatedVO2max?.toFixed(1) ?? 'N/A'}`
  );

  return sessionId;
}

/**
 * Get VO2max sessions with optional filtering
 *
 * Supports filtering by:
 * - Date range (start_date, end_date)
 * - Protocol type (norwegian_4x4, zone2)
 * - Pagination (limit, offset)
 *
 * @param filters - Filter options
 * @returns Array of VO2max sessions with workout and user data
 */
export function getVO2maxSessions(filters: VO2maxSessionFilters): any[] {
  const { user_id, start_date, end_date, protocol_type, limit = 50, offset = 0 } = filters;

  // Enforce maximum limit of 200
  const effectiveLimit = Math.min(limit, 200);

  // Build query with filters
  let query = `
    SELECT
      v.*,
      w.date,
      w.user_id,
      w.status
    FROM vo2max_sessions v
    JOIN workouts w ON v.workout_id = w.id
    WHERE w.user_id = ?
  `;

  const params: any[] = [user_id];

  // Add date range filters
  if (start_date) {
    query += ` AND w.date >= ?`;
    params.push(start_date);
  }

  if (end_date) {
    query += ` AND w.date <= ?`;
    params.push(end_date);
  }

  // Add protocol filter (convert to database format)
  if (protocol_type) {
    const dbProtocol = protocol_type === 'norwegian_4x4' ? '4x4' : 'zone2';
    query += ` AND v.protocol = ?`;
    params.push(dbProtocol);
  }

  // Add ordering and pagination
  query += ` ORDER BY w.date DESC LIMIT ? OFFSET ?`;
  params.push(effectiveLimit, offset);

  const sessions = db.prepare(query).all(...params);

  return sessions;
}

/**
 * Get VO2max progression over time for a user
 *
 * Returns all VO2max estimates ordered by date, allowing tracking
 * of cardiovascular fitness improvements.
 *
 * @param userId - User ID
 * @param startDate - Optional start date filter (YYYY-MM-DD)
 * @param endDate - Optional end date filter (YYYY-MM-DD)
 * @returns Array of progression data points
 */
export function getVO2maxProgression(
  userId: number,
  startDate?: string,
  endDate?: string
): VO2maxProgressionPoint[] {
  let query = `
    SELECT
      w.date,
      v.estimated_vo2max,
      v.protocol
    FROM vo2max_sessions v
    JOIN workouts w ON v.workout_id = w.id
    WHERE w.user_id = ?
      AND v.estimated_vo2max IS NOT NULL
  `;

  const params: any[] = [userId];

  if (startDate) {
    query += ` AND w.date >= ?`;
    params.push(startDate);
  }

  if (endDate) {
    query += ` AND w.date <= ?`;
    params.push(endDate);
  }

  query += ` ORDER BY w.date ASC`;

  const results = db.prepare(query).all(...params) as Array<{
    date: string;
    estimated_vo2max: number;
    protocol: string;
  }>;

  return results.map((row) => ({
    date: row.date,
    estimated_vo2max: row.estimated_vo2max,
    protocol: row.protocol,
  }));
}

/**
 * Get a single VO2max session by ID
 *
 * @param sessionId - VO2max session ID
 * @param userId - User ID (for ownership validation)
 * @returns VO2max session or null if not found
 */
export function getVO2maxSessionById(sessionId: number, userId: number): any | null {
  const session = db
    .prepare(
      `
      SELECT
        v.*,
        w.date,
        w.user_id,
        w.status
      FROM vo2max_sessions v
      JOIN workouts w ON v.workout_id = w.id
      WHERE v.id = ? AND w.user_id = ?
    `
    )
    .get(sessionId, userId);

  return session || null;
}
