/**
 * Get 1RM progression for a specific exercise over time (T047)
 *
 * Calculates estimated 1RM using Epley formula with RIR adjustment:
 * 1RM = weight * (1 + (reps - rir) / 30)
 *
 * @param userId User ID
 * @param exerciseId Exercise ID
 * @param startDate Start date (YYYY-MM-DD)
 * @param endDate End date (YYYY-MM-DD)
 * @returns Array of {date, estimated_1rm} sorted by date
 */
export function get1RMProgression(
  userId: number,
  exerciseId: number,
  startDate: string,
  endDate: string
): OneRMProgressionPoint[] {
  const results = stmt1RMProgression.all(userId, exerciseId, startDate, endDate) as Array<{
    date: string;
    estimated_1rm: number;
  }>;

  return results.map((row) => ({
    date: row.date,
    estimated_1rm: Math.round(row.estimated_1rm * 10) / 10, // Round to 1 decimal place
  }));
}

/**
 * Get volume trends for a muscle group over time (T048)
 *
 * Groups sets by ISO week and includes MEV/MAV/MRV landmarks.
 *
 * @param userId User ID
 * @param muscleGroup Muscle group name (e.g., 'chest', 'back_lats')
 * @param startDate Start date (YYYY-MM-DD)
 * @param endDate End date (YYYY-MM-DD)
 * @returns Array of {week, total_sets, mev, mav, mrv} sorted by week
 */
export function getVolumeTrends(
  userId: number,
  muscleGroup: string,
  startDate: string,
  endDate: string
): VolumeTrendsPoint[] {
  const results = stmtVolumeTrends.all(userId, startDate, endDate, muscleGroup) as Array<{
    week: string;
    total_sets: number;
  }>;

  // Get volume landmarks for this muscle group
  const landmarks = VOLUME_LANDMARKS[muscleGroup] || { mev: 0, mav: 0, mrv: 0 };

  return results.map((row) => ({
    week: row.week,
    total_sets: row.total_sets,
    mev: landmarks.mev,
    mav: landmarks.mav,
    mrv: landmarks.mrv,
  }));
}

/**
 * Get consistency metrics for a user (T049)
 *
 * Calculates:
 * - adherence_rate: completed_workouts / total_workouts (scheduled)
 * - avg_session_duration: mean duration in seconds
 * - total_workouts: count of all workouts
 *
 * @param userId User ID
 * @returns Consistency metrics object
 */
export function getConsistencyMetrics(userId: number): ConsistencyMetrics {
  const result = stmtConsistencyMetrics.get(userId) as {
    completed_workouts: number;
    total_workouts: number;
    avg_session_duration: number | null;
  };

  // Calculate adherence rate (avoid division by zero)
  const adherenceRate =
    result.total_workouts > 0 ? result.completed_workouts / result.total_workouts : 0;

  return {
    adherence_rate: Math.round(adherenceRate * 1000) / 1000, // Round to 3 decimal places
    avg_session_duration: result.avg_session_duration || 0,
    total_workouts: result.total_workouts,
  };
}

/**
 * Volume Analytics Service (T041-T045)
 *
 * Provides volume analytics:
 * - Current week volume tracking (completed + planned)
 * - Volume history over multiple weeks
 * - Zone classification (below_mev, adequate, optimal, above_mrv)
 */

import { db } from '../database/db.js';

/**
 * Volume landmarks for muscle groups (from Renaissance Periodization)
 */
const VOLUME_LANDMARKS: Record<string, { mev: number; mav: number; mrv: number }> = {
  chest: { mev: 8, mav: 14, mrv: 22 },
  back: { mev: 10, mav: 16, mrv: 26 },
  back_lats: { mev: 10, mav: 16, mrv: 26 },
  back_traps: { mev: 6, mav: 12, mrv: 20 },
  shoulders: { mev: 8, mav: 14, mrv: 22 },
  shoulders_front: { mev: 4, mav: 8, mrv: 14 },
  shoulders_side: { mev: 8, mav: 16, mrv: 26 },
  shoulders_rear: { mev: 8, mav: 14, mrv: 22 },
  front_delts: { mev: 4, mav: 8, mrv: 14 },
  side_delts: { mev: 8, mav: 16, mrv: 26 },
  rear_delts: { mev: 8, mav: 14, mrv: 22 },
  biceps: { mev: 6, mav: 12, mrv: 20 },
  triceps: { mev: 6, mav: 12, mrv: 22 },
  quads: { mev: 8, mav: 14, mrv: 24 },
  hamstrings: { mev: 6, mav: 12, mrv: 20 },
  glutes: { mev: 6, mav: 12, mrv: 20 },
  calves: { mev: 8, mav: 14, mrv: 22 },
  abs: { mev: 8, mav: 16, mrv: 28 },
};

/**
 * Muscle group volume tracking data
 */
export interface MuscleGroupVolumeTracking {
  muscle_group: string;
  completed_sets: number;
  planned_sets: number;
  remaining_sets: number;
  mev: number;
  mav: number;
  mrv: number;
  completion_percentage: number;
  zone: 'below_mev' | 'adequate' | 'optimal' | 'above_mrv' | 'on_track';
  warning: string | null;
}

/**
 * Current week volume response
 */
export interface CurrentWeekVolume {
  week_start: string;
  week_end: string;
  muscle_groups: MuscleGroupVolumeTracking[];
}

/**
 * Historical volume data point
 */
export interface HistoricalVolume {
  muscle_group: string;
  completed_sets: number;
  mev: number;
  mav: number;
  mrv: number;
}

/**
 * Volume trends response (historical)
 */
export interface VolumeTrendsResponse {
  weeks: Array<{
    week_start: string;
    muscle_groups: HistoricalVolume[];
  }>;
}

/**
 * Program volume analysis data
 */
export interface ProgramVolumeAnalysis {
  program_id: number;
  mesocycle_phase: 'mev' | 'mav' | 'mrv' | 'deload';
  muscle_groups: Array<{
    muscle_group: string;
    planned_weekly_sets: number;
    mev: number;
    mav: number;
    mrv: number;
    zone: 'below_mev' | 'adequate' | 'optimal' | 'above_mrv';
    warning: string | null;
  }>;
}

/**
 * Get ISO week boundaries (Monday to Sunday) for a given date
 */
function getISOWeekBoundaries(date: Date): { week_start: string; week_end: string } {
  // Get day of week (0 = Sunday, 1 = Monday, ..., 6 = Saturday)
  const dayOfWeek = date.getDay();

  // Calculate Monday of the week (ISO week starts on Monday)
  const monday = new Date(date);
  const daysToMonday = dayOfWeek === 0 ? -6 : 1 - dayOfWeek; // If Sunday, go back 6 days
  monday.setDate(date.getDate() + daysToMonday);

  // Calculate Sunday of the week
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);

  const weekStart = monday.toISOString().split('T')[0];
  const weekEnd = sunday.toISOString().split('T')[0];

  // Ensure non-undefined values
  if (!weekStart || !weekEnd) {
    throw new Error('Failed to calculate ISO week boundaries');
  }

  return {
    week_start: weekStart,
    week_end: weekEnd,
  };
}

/**
 * Classify volume zone based on completed sets
 */
function classifyZone(
  completedSets: number,
  mev: number,
  mav: number,
  mrv: number
): 'below_mev' | 'adequate' | 'optimal' | 'above_mrv' {
  if (completedSets < mev) {
    return 'below_mev';
  } else if (completedSets < mav) {
    return 'adequate';
  } else if (completedSets <= mrv) {
    return 'optimal';
  } else {
    return 'above_mrv';
  }
}

/**
 * Classify zone with on_track for current week tracking
 */
function classifyZoneWithOnTrack(
  completedSets: number,
  plannedSets: number,
  mev: number,
  mav: number,
  mrv: number
): 'below_mev' | 'adequate' | 'optimal' | 'above_mrv' | 'on_track' {
  // If planned sets are in optimal range and completion >= 50%, mark as on_track
  if (plannedSets >= mav && plannedSets <= mrv && completedSets >= plannedSets * 0.5) {
    return 'on_track';
  }

  // If planned sets are in adequate range and completion >= 50%, mark as on_track
  if (plannedSets >= mev && plannedSets < mav && completedSets >= plannedSets * 0.5) {
    return 'on_track';
  }

  // Otherwise use standard zone classification
  return classifyZone(completedSets, mev, mav, mrv);
}

/**
 * Generate warning message based on zone
 */
function generateWarning(zone: string, muscleGroup: string): string | null {
  if (zone === 'below_mev') {
    return `${muscleGroup} volume is below minimum effective volume (MEV). Increase sets for growth.`;
  } else if (zone === 'above_mrv') {
    return `${muscleGroup} volume exceeds maximum recoverable volume (MRV). Risk of overtraining.`;
  }
  return null;
}

/**
 * Get current week volume tracking (T017)
 *
 * Calculates:
 * - Completed sets from workouts with status='completed' in current ISO week
 * - Planned sets from program_exercises for all program_days in current week
 * - Zone classification based on completed sets
 * - Full set counting: Each set counts fully toward ALL muscle groups
 *
 * @param userId User ID
 * @returns Current week volume data with zone classification
 */
export function getCurrentWeekVolume(userId: number): CurrentWeekVolume {
  const now = new Date();
  const { week_start, week_end } = getISOWeekBoundaries(now);

  // Query completed sets grouped by muscle group for current week
  const completedQuery = db.prepare(`
    SELECT
      mg.value as muscle_group,
      COUNT(s.id) as completed_sets
    FROM sets s
    JOIN workouts w ON s.workout_id = w.id
    JOIN exercises e ON s.exercise_id = e.id
    JOIN json_each(e.muscle_groups) mg
    WHERE w.user_id = ?
      AND w.status = 'completed'
      AND w.date >= ?
      AND w.date <= ?
    GROUP BY mg.value
  `);

  const completedResults = completedQuery.all(userId, week_start, week_end) as Array<{
    muscle_group: string;
    completed_sets: number;
  }>;

  // Query planned sets from active program for current week
  // Get active program first
  const activeProgramQuery = db.prepare(`
    SELECT id FROM programs
    WHERE user_id = ?
    ORDER BY created_at DESC
    LIMIT 1
  `);

  const activeProgram = activeProgramQuery.get(userId) as { id: number } | undefined;

  let plannedResults: Array<{ muscle_group: string; planned_sets: number }> = [];

  if (activeProgram) {
    // Calculate day of week for current week (1-7, Monday = 1)
    // Get all program days for this week and sum their planned sets
    const plannedQuery = db.prepare(`
      SELECT
        mg.value as muscle_group,
        SUM(pe.sets) as planned_sets
      FROM program_exercises pe
      JOIN program_days pd ON pe.program_day_id = pd.id
      JOIN exercises e ON pe.exercise_id = e.id
      JOIN json_each(e.muscle_groups) mg
      WHERE pd.program_id = ?
      GROUP BY mg.value
    `);

    plannedResults = plannedQuery.all(activeProgram.id) as Array<{
      muscle_group: string;
      planned_sets: number;
    }>;
  }

  // Merge completed and planned data
  const muscleGroupMap = new Map<string, MuscleGroupVolumeTracking>();

  // Process completed sets
  completedResults.forEach((row) => {
    const landmarks = VOLUME_LANDMARKS[row.muscle_group] || { mev: 0, mav: 0, mrv: 0 };
    muscleGroupMap.set(row.muscle_group, {
      muscle_group: row.muscle_group,
      completed_sets: row.completed_sets,
      planned_sets: 0,
      remaining_sets: 0,
      mev: landmarks.mev,
      mav: landmarks.mav,
      mrv: landmarks.mrv,
      completion_percentage: 0,
      zone: 'below_mev',
      warning: null,
    });
  });

  // Process planned sets
  plannedResults.forEach((row) => {
    const landmarks = VOLUME_LANDMARKS[row.muscle_group] || { mev: 0, mav: 0, mrv: 0 };
    const existing = muscleGroupMap.get(row.muscle_group);

    if (existing) {
      existing.planned_sets = row.planned_sets;
    } else {
      muscleGroupMap.set(row.muscle_group, {
        muscle_group: row.muscle_group,
        completed_sets: 0,
        planned_sets: row.planned_sets,
        remaining_sets: 0,
        mev: landmarks.mev,
        mav: landmarks.mav,
        mrv: landmarks.mrv,
        completion_percentage: 0,
        zone: 'below_mev',
        warning: null,
      });
    }
  });

  // Calculate derived fields and classify zones
  const muscleGroups: MuscleGroupVolumeTracking[] = [];
  muscleGroupMap.forEach((mg) => {
    mg.remaining_sets = Math.max(0, mg.planned_sets - mg.completed_sets);
    mg.completion_percentage = mg.planned_sets > 0
      ? Math.round((mg.completed_sets / mg.planned_sets) * 100 * 10) / 10
      : 0;

    mg.zone = classifyZoneWithOnTrack(
      mg.completed_sets,
      mg.planned_sets,
      mg.mev,
      mg.mav,
      mg.mrv
    );

    mg.warning = generateWarning(mg.zone, mg.muscle_group);

    muscleGroups.push(mg);
  });

  // Sort by muscle group name
  muscleGroups.sort((a, b) => a.muscle_group.localeCompare(b.muscle_group));

  return {
    week_start,
    week_end,
    muscle_groups: muscleGroups,
  };
}

/**
 * Get volume history over multiple weeks (T018)
 *
 * @param userId User ID
 * @param weeks Number of weeks to retrieve (default: 8, max: 52)
 * @param muscleGroupFilter Optional muscle group filter
 * @returns Historical volume data grouped by week
 */
export function getVolumeHistory(
  userId: number,
  weeks: number = 8,
  muscleGroupFilter?: string
): VolumeTrendsResponse {
  // Validate weeks parameter
  if (weeks < 1 || weeks > 52) {
    throw new Error('Weeks parameter must be between 1 and 52');
  }

  // Calculate start date (weeks ago from today)
  const endDate = new Date();
  const startDate = new Date();
  startDate.setDate(endDate.getDate() - (weeks * 7));

  // Query volume by week
  const query = db.prepare(`
    SELECT
      w.date,
      mg.value as muscle_group,
      COUNT(s.id) as completed_sets
    FROM sets s
    JOIN workouts w ON s.workout_id = w.id
    JOIN exercises e ON s.exercise_id = e.id
    JOIN json_each(e.muscle_groups) mg
    WHERE w.user_id = ?
      AND w.status = 'completed'
      AND w.date >= ?
      AND w.date <= ?
      ${muscleGroupFilter ? 'AND mg.value = ?' : ''}
    GROUP BY w.date, mg.value
    ORDER BY w.date
  `);

  const params = [userId, startDate.toISOString().split('T')[0], endDate.toISOString().split('T')[0]];
  if (muscleGroupFilter) {
    params.push(muscleGroupFilter);
  }

  const results = query.all(...params) as Array<{
    date: string;
    muscle_group: string;
    completed_sets: number;
  }>;

  // Group by week
  const weekMap = new Map<string, Map<string, number>>();

  results.forEach((row) => {
    const date = new Date(row.date);
    const { week_start } = getISOWeekBoundaries(date);

    if (!weekMap.has(week_start)) {
      weekMap.set(week_start, new Map());
    }

    const muscleGroupMap = weekMap.get(week_start)!;
    const currentSets = muscleGroupMap.get(row.muscle_group) || 0;
    muscleGroupMap.set(row.muscle_group, currentSets + row.completed_sets);
  });

  // Convert to response format
  const weekArray: Array<{
    week_start: string;
    muscle_groups: HistoricalVolume[];
  }> = [];

  weekMap.forEach((muscleGroupMap, week_start) => {
    const muscleGroups: HistoricalVolume[] = [];

    muscleGroupMap.forEach((completed_sets, muscle_group) => {
      const landmarks = VOLUME_LANDMARKS[muscle_group] || { mev: 0, mav: 0, mrv: 0 };
      muscleGroups.push({
        muscle_group,
        completed_sets,
        mev: landmarks.mev,
        mav: landmarks.mav,
        mrv: landmarks.mrv,
      });
    });

    // Sort muscle groups alphabetically
    muscleGroups.sort((a, b) => a.muscle_group.localeCompare(b.muscle_group));

    weekArray.push({
      week_start,
      muscle_groups: muscleGroups,
    });
  });

  // Sort weeks chronologically (oldest first)
  weekArray.sort((a, b) => a.week_start.localeCompare(b.week_start));

  return {
    weeks: weekArray,
  };
}

/**
 * Get program volume analysis (T019)
 *
 * Analyzes planned volume for the active program and classifies zones.
 *
 * @param userId User ID
 * @returns Program volume analysis or null if no active program
 */
export function getProgramVolumeAnalysis(userId: number): ProgramVolumeAnalysis | null {
  // Get active program
  const activeProgramQuery = db.prepare(`
    SELECT id, mesocycle_phase
    FROM programs
    WHERE user_id = ?
    ORDER BY created_at DESC
    LIMIT 1
  `);

  const activeProgram = activeProgramQuery.get(userId) as
    { id: number; mesocycle_phase: 'mev' | 'mav' | 'mrv' | 'deload' } | undefined;

  if (!activeProgram) {
    return null;
  }

  // Query planned weekly sets by muscle group
  const plannedQuery = db.prepare(`
    SELECT
      mg.value as muscle_group,
      SUM(pe.sets) as planned_weekly_sets
    FROM program_exercises pe
    JOIN program_days pd ON pe.program_day_id = pd.id
    JOIN exercises e ON pe.exercise_id = e.id
    JOIN json_each(e.muscle_groups) mg
    WHERE pd.program_id = ?
    GROUP BY mg.value
  `);

  const results = plannedQuery.all(activeProgram.id) as Array<{
    muscle_group: string;
    planned_weekly_sets: number;
  }>;

  // Classify zones
  const muscleGroups = results.map((row) => {
    const landmarks = VOLUME_LANDMARKS[row.muscle_group] || { mev: 0, mav: 0, mrv: 0 };
    const zone = classifyZone(row.planned_weekly_sets, landmarks.mev, landmarks.mav, landmarks.mrv);

    return {
      muscle_group: row.muscle_group,
      planned_weekly_sets: row.planned_weekly_sets,
      mev: landmarks.mev,
      mav: landmarks.mav,
      mrv: landmarks.mrv,
      zone,
      warning: generateWarning(zone, row.muscle_group),
    };
  });

  // Sort by muscle group name
  muscleGroups.sort((a, b) => a.muscle_group.localeCompare(b.muscle_group));

  return {
    program_id: activeProgram.id,
    mesocycle_phase: activeProgram.mesocycle_phase,
    muscle_groups: muscleGroups,
  };
}

/**
 * Recovery Assessment Service
 *
 * Business logic for daily recovery check-in and auto-regulation:
 * - 3-question assessment (sleep, soreness, motivation) on 1-5 scale
 * - Total score calculation (3-15 range)
 * - Volume adjustment logic per FR-009
 */

import { stmtCreateRecoveryAssessment, calculateVolumeAdjustment, db } from '../database/db.js';

/**
 * Recovery assessment response interface
 */
export interface RecoveryAssessmentResponse {
  total_score: number;
  volume_adjustment: 'none' | 'reduce_1_set' | 'reduce_2_sets' | 'rest_day';
}

/**
 * Create a new recovery assessment
 *
 * Calculates total recovery score from 3 subscores (sleep quality,
 * muscle soreness, mental motivation) and determines volume adjustment
 * based on auto-regulation logic per FR-008 and FR-009.
 *
 * Auto-Regulation Logic (FR-009):
 * - Score 12-15: No adjustment (good recovery)
 * - Score 9-11: Reduce by 1 set per exercise
 * - Score 6-8: Reduce by 2 sets per exercise
 * - Score 3-5: Rest day recommended
 *
 * @param userId - ID of the user submitting assessment
 * @param date - Assessment date in ISO format (YYYY-MM-DD)
 * @param sleepQuality - Sleep quality rating (1-5 scale)
 * @param muscleSoreness - Muscle soreness rating (1-5 scale, inverted)
 * @param mentalMotivation - Mental motivation rating (1-5 scale)
 * @returns RecoveryAssessmentResponse with total_score and volume_adjustment
 */
export function createAssessment(
  userId: number,
  date: string,
  sleepQuality: number,
  muscleSoreness: number,
  mentalMotivation: number
): RecoveryAssessmentResponse {
  // Validate input ranges (1-5 per FR-008)
  if (sleepQuality < 1 || sleepQuality > 5) {
    throw new Error('Sleep quality must be between 1 and 5');
  }
  if (muscleSoreness < 1 || muscleSoreness > 5) {
    throw new Error('Muscle soreness must be between 1 and 5');
  }
  if (mentalMotivation < 1 || mentalMotivation > 5) {
    throw new Error('Mental motivation must be between 1 and 5');
  }

  // Validate date format (ISO YYYY-MM-DD)
  const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
  if (!dateRegex.test(date)) {
    throw new Error('Date must be in ISO format (YYYY-MM-DD)');
  }

  // Calculate total score (sum of 3 subscores, range 3-15)
  const totalScore = sleepQuality + muscleSoreness + mentalMotivation;

  // Determine volume adjustment based on total score (FR-009)
  const volumeAdjustment = calculateVolumeAdjustment(totalScore);

  // Check if assessment already exists for this user and date
  const existing = db
    .prepare('SELECT id FROM recovery_assessments WHERE user_id = ? AND date = ?')
    .get(userId, date) as { id: number } | undefined;

  const timestamp = Date.now();

  if (existing) {
    // Update existing assessment
    db.prepare(`
      UPDATE recovery_assessments
      SET sleep_quality = ?,
          muscle_soreness = ?,
          mental_motivation = ?,
          total_score = ?,
          volume_adjustment = ?,
          timestamp = ?
      WHERE id = ?
    `).run(
      sleepQuality,
      muscleSoreness,
      mentalMotivation,
      totalScore,
      volumeAdjustment,
      timestamp,
      existing.id
    );
    console.log(`Recovery assessment updated: id=${existing.id}, user=${userId}, date=${date}`);
  } else {
    // Insert new recovery assessment
    stmtCreateRecoveryAssessment.run(
      userId,
      date,
      sleepQuality,
      muscleSoreness,
      mentalMotivation,
      totalScore,
      volumeAdjustment,
      timestamp
    );
    console.log(`Recovery assessment created: user=${userId}, date=${date}`);
  }

  // Log assessment for monitoring
  console.log(
    `Recovery assessment created: user=${userId}, date=${date}, ` +
      `score=${totalScore} (sleep=${sleepQuality}, soreness=${muscleSoreness}, ` +
      `motivation=${mentalMotivation}), adjustment=${volumeAdjustment}`
  );

  return {
    total_score: totalScore,
    volume_adjustment: volumeAdjustment,
  };
}

/**
 * Exercise Service
 *
 * Handles exercise library filtering and retrieval for program planning
 */

import { db } from '../database/db.js';

/**
 * Exercise entity with typed fields
 */
export interface Exercise {
  id: number;
  name: string;
  primary_muscle_group: string;
  secondary_muscle_groups: string[]; // Parsed from JSON
  equipment: 'barbell' | 'dumbbell' | 'cable' | 'machine' | 'bodyweight';
  movement_pattern: 'compound' | 'isolation';
  difficulty: 'beginner' | 'intermediate' | 'advanced';
  default_sets: number;
  default_reps: string;
  default_rir: number;
  description: string;
}

/**
 * Exercise filters
 */
export interface ExerciseFilters {
  muscle_group?: string;
  equipment?: 'barbell' | 'dumbbell' | 'cable' | 'machine' | 'bodyweight';
  movement_pattern?: 'compound' | 'isolation';
  difficulty?: 'beginner' | 'intermediate' | 'advanced';
}

/**
 * Valid muscle groups for validation
 */
const VALID_MUSCLE_GROUPS = [
  'chest',
  'lats',
  'mid_back',
  'rear_delts',
  'front_delts',
  'side_delts',
  'triceps',
  'biceps',
  'forearms',
  'quads',
  'hamstrings',
  'glutes',
  'calves',
  'abs',
  'obliques',
];

/**
 * Get exercises with optional filtering
 *
 * @param filters - Optional filters for muscle_group, equipment, movement_pattern, difficulty
 * @returns Array of exercises matching filters
 */
export function getExercises(filters: ExerciseFilters = {}): Exercise[] {
  // Validate muscle_group if provided
  if (filters.muscle_group && !VALID_MUSCLE_GROUPS.includes(filters.muscle_group)) {
    throw new Error(
      `Invalid muscle_group: ${filters.muscle_group}. Valid options: ${VALID_MUSCLE_GROUPS.join(', ')}`
    );
  }

  // Build WHERE clause dynamically
  const conditions: string[] = [];
  const params: any[] = [];

  // Filter by muscle group (check both primary and secondary)
  if (filters.muscle_group) {
    conditions.push(
      '(primary_muscle_group = ? OR secondary_muscle_groups LIKE ?)'
    );
    params.push(filters.muscle_group);
    params.push(`%"${filters.muscle_group}"%`); // JSON array contains check
  }

  // Filter by equipment
  if (filters.equipment) {
    conditions.push('equipment = ?');
    params.push(filters.equipment);
  }

  // Filter by movement pattern
  if (filters.movement_pattern) {
    conditions.push('movement_pattern = ?');
    params.push(filters.movement_pattern);
  }

  // Filter by difficulty
  if (filters.difficulty) {
    conditions.push('difficulty = ?');
    params.push(filters.difficulty);
  }

  // Build SQL query
  const whereClause = conditions.length > 0 ? `WHERE ${conditions.join(' AND ')}` : '';
  const sql = `
    SELECT
      id,
      name,
      primary_muscle_group,
      secondary_muscle_groups,
      equipment,
      movement_pattern,
      difficulty,
      default_sets,
      default_reps,
      default_rir,
      description
    FROM exercises
    ${whereClause}
    ORDER BY name
  `;

  // Execute query
  const rows = db.prepare(sql).all(...params) as any[];

  // Parse secondary_muscle_groups JSON for each exercise
  return rows.map((row) => ({
    ...row,
    secondary_muscle_groups: JSON.parse(row.secondary_muscle_groups || '[]'),
  }));
}

/**
 * Get exercise by ID
 *
 * @param id - Exercise ID
 * @returns Exercise details or undefined if not found
 */
export function getExerciseById(id: number): Exercise | undefined {
  const sql = `
    SELECT
      id,
      name,
      primary_muscle_group,
      secondary_muscle_groups,
      equipment,
      movement_pattern,
      difficulty,
      default_sets,
      default_reps,
      default_rir,
      description
    FROM exercises
    WHERE id = ?
  `;

  const row = db.prepare(sql).get(id) as any;

  if (!row) {
    return undefined;
  }

  // Parse secondary_muscle_groups JSON
  return {
    ...row,
    secondary_muscle_groups: JSON.parse(row.secondary_muscle_groups || '[]'),
  };
}

