/**
 * Volume Analytics Service (T041-T045)
 *
 * Provides volume analytics:
 * - Current week volume tracking (completed + planned)
 * - Volume history over multiple weeks
 * - Zone classification (below_mev, adequate, optimal, above_mrv)
 */

import { db } from '../database/db.js';

/**
 * Volume landmarks for muscle groups (from Renaissance Periodization)
 */
const VOLUME_LANDMARKS: Record<string, { mev: number; mav: number; mrv: number }> = {
  // Primary muscle groups
  chest: { mev: 8, mav: 14, mrv: 22 },
  biceps: { mev: 6, mav: 12, mrv: 20 },
  triceps: { mev: 6, mav: 12, mrv: 22 },
  quads: { mev: 8, mav: 14, mrv: 24 },
  hamstrings: { mev: 6, mav: 12, mrv: 20 },
  glutes: { mev: 6, mav: 12, mrv: 20 },
  calves: { mev: 8, mav: 14, mrv: 22 },
  abs: { mev: 8, mav: 16, mrv: 28 },

  // Back muscles (database uses lats, traps, mid_back, lower_back)
  lats: { mev: 10, mav: 16, mrv: 26 },
  traps: { mev: 6, mav: 12, mrv: 20 },
  mid_back: { mev: 10, mav: 16, mrv: 26 },
  lower_back: { mev: 6, mav: 12, mrv: 20 },

  // Shoulder muscles (database uses front_delts, side_delts, rear_delts)
  front_delts: { mev: 4, mav: 8, mrv: 14 },
  side_delts: { mev: 8, mav: 16, mrv: 26 },
  rear_delts: { mev: 8, mav: 14, mrv: 22 },

  // Supporting muscles
  core: { mev: 8, mav: 16, mrv: 28 },
  obliques: { mev: 6, mav: 12, mrv: 20 },
  forearms: { mev: 4, mav: 8, mrv: 16 },
  brachialis: { mev: 4, mav: 8, mrv: 14 },
  hip_flexors: { mev: 4, mav: 8, mrv: 14 },

  // Legacy/compatibility names (may not be in database)
  back: { mev: 10, mav: 16, mrv: 26 },
  shoulders: { mev: 8, mav: 14, mrv: 22 },
};

/**
 * Muscle group volume tracking data
 */
export interface MuscleGroupVolumeTracking {
  muscle_group: string;
  completed_sets: number;
  planned_sets: number;
  remaining_sets: number;
  mev: number;
  mav: number;
  mrv: number;
  completion_percentage: number;
  zone: 'below_mev' | 'adequate' | 'optimal' | 'above_mrv' | 'on_track';
  warning: string | null;
}

/**
 * Current week volume response
 */
export interface CurrentWeekVolume {
  week_start: string;
  week_end: string;
  muscle_groups: MuscleGroupVolumeTracking[];
}

/**
 * Historical volume data point
 */
export interface HistoricalVolume {
  muscle_group: string;
  completed_sets: number;
  mev: number;
  mav: number;
  mrv: number;
}

/**
 * Volume trends response (historical)
 */
export interface VolumeTrendsResponse {
  weeks: Array<{
    week_start: string;
    muscle_groups: HistoricalVolume[];
  }>;
}

/**
 * Program volume analysis data
 */
export interface ProgramVolumeAnalysis {
  program_id: number;
  mesocycle_phase: 'mev' | 'mav' | 'mrv' | 'deload';
  muscle_groups: Array<{
    muscle_group: string;
    planned_weekly_sets: number;
    mev: number;
    mav: number;
    mrv: number;
    zone: 'below_mev' | 'adequate' | 'optimal' | 'above_mrv';
    warning: string | null;
  }>;
}

/**
 * Get ISO week boundaries (Monday to Sunday) for a given date
 */
function getISOWeekBoundaries(date: Date): { week_start: string; week_end: string } {
  // Get day of week (0 = Sunday, 1 = Monday, ..., 6 = Saturday)
  const dayOfWeek = date.getDay();

  // Calculate Monday of the week (ISO week starts on Monday)
  const monday = new Date(date);
  const daysToMonday = dayOfWeek === 0 ? -6 : 1 - dayOfWeek; // If Sunday, go back 6 days
  monday.setDate(date.getDate() + daysToMonday);

  // Calculate Sunday of the week
  const sunday = new Date(monday);
  sunday.setDate(monday.getDate() + 6);

  const weekStart = monday.toISOString().split('T')[0];
  const weekEnd = sunday.toISOString().split('T')[0];

  // Ensure non-undefined values
  if (!weekStart || !weekEnd) {
    throw new Error('Failed to calculate ISO week boundaries');
  }

  return {
    week_start: weekStart,
    week_end: weekEnd,
  };
}

/**
 * Classify volume zone based on completed sets
 */
function classifyZone(
  completedSets: number,
  mev: number,
  mav: number,
  mrv: number
): 'below_mev' | 'adequate' | 'optimal' | 'above_mrv' {
  if (completedSets < mev) {
    return 'below_mev';
  } else if (completedSets < mav) {
    return 'adequate';
  } else if (completedSets <= mrv) {
    return 'optimal';
  } else {
    return 'above_mrv';
  }
}

/**
 * Classify zone with on_track for current week tracking
 */
function classifyZoneWithOnTrack(
  completedSets: number,
  plannedSets: number,
  mev: number,
  mav: number,
  mrv: number
): 'below_mev' | 'adequate' | 'optimal' | 'above_mrv' | 'on_track' {
  // If planned sets are in optimal range and completion >= 50%, mark as on_track
  if (plannedSets >= mav && plannedSets <= mrv && completedSets >= plannedSets * 0.5) {
    return 'on_track';
  }

  // If planned sets are in adequate range and completion >= 50%, mark as on_track
  if (plannedSets >= mev && plannedSets < mav && completedSets >= plannedSets * 0.5) {
    return 'on_track';
  }

  // Otherwise use standard zone classification
  return classifyZone(completedSets, mev, mav, mrv);
}

/**
 * Generate warning message based on zone
 */
function generateWarning(zone: string, muscleGroup: string): string | null {
  if (zone === 'below_mev') {
    return `${muscleGroup} volume is below minimum effective volume (MEV). Increase sets for growth.`;
  } else if (zone === 'above_mrv') {
    return `${muscleGroup} volume exceeds maximum recoverable volume (MRV). Risk of overtraining.`;
  }
  return null;
}

/**
 * Get current week volume tracking (T017)
 *
 * Calculates:
 * - Completed sets from workouts with status='completed' in current ISO week
 * - Planned sets from program_exercises for all program_days in current week
 * - Zone classification based on completed sets
 * - Full set counting: Each set counts fully toward ALL muscle groups
 *
 * @param userId User ID
 * @returns Current week volume data with zone classification
 */
export function getCurrentWeekVolume(userId: number): CurrentWeekVolume {
  const now = new Date();
  const { week_start, week_end } = getISOWeekBoundaries(now);

  // Query completed sets grouped by muscle group for current week
  const completedQuery = db.prepare(`
    SELECT
      mg.value as muscle_group,
      COUNT(s.id) as completed_sets
    FROM sets s
    JOIN workouts w ON s.workout_id = w.id
    JOIN exercises e ON s.exercise_id = e.id
    JOIN json_each(e.muscle_groups) mg
    WHERE w.user_id = ?
      AND w.status = 'completed'
      AND w.date >= ?
      AND w.date <= ?
    GROUP BY mg.value
  `);

  const completedResults = completedQuery.all(userId, week_start, week_end) as Array<{
    muscle_group: string;
    completed_sets: number;
  }>;

  // Query planned sets from active program for current week
  // Get active program first
  const activeProgramQuery = db.prepare(`
    SELECT id FROM programs
    WHERE user_id = ?
    ORDER BY created_at DESC
    LIMIT 1
  `);

  const activeProgram = activeProgramQuery.get(userId) as { id: number } | undefined;

  let plannedResults: Array<{ muscle_group: string; planned_sets: number }> = [];

  if (activeProgram) {
    // Calculate day of week for current week (1-7, Monday = 1)
    // Get all program days for this week and sum their planned sets
    const plannedQuery = db.prepare(`
      SELECT
        mg.value as muscle_group,
        SUM(pe.sets) as planned_sets
      FROM program_exercises pe
      JOIN program_days pd ON pe.program_day_id = pd.id
      JOIN exercises e ON pe.exercise_id = e.id
      JOIN json_each(e.muscle_groups) mg
      WHERE pd.program_id = ?
      GROUP BY mg.value
    `);

    plannedResults = plannedQuery.all(activeProgram.id) as Array<{
      muscle_group: string;
      planned_sets: number;
    }>;
  }

  // Merge completed and planned data
  const muscleGroupMap = new Map<string, MuscleGroupVolumeTracking>();

  // Process completed sets
  completedResults.forEach((row) => {
    const landmarks = VOLUME_LANDMARKS[row.muscle_group] || { mev: 0, mav: 0, mrv: 0 };
    muscleGroupMap.set(row.muscle_group, {
      muscle_group: row.muscle_group,
      completed_sets: row.completed_sets,
      planned_sets: 0,
      remaining_sets: 0,
      mev: landmarks.mev,
      mav: landmarks.mav,
      mrv: landmarks.mrv,
      completion_percentage: 0,
      zone: 'below_mev',
      warning: null,
    });
  });

  // Process planned sets
  plannedResults.forEach((row) => {
    const landmarks = VOLUME_LANDMARKS[row.muscle_group] || { mev: 0, mav: 0, mrv: 0 };
    const existing = muscleGroupMap.get(row.muscle_group);

    if (existing) {
      existing.planned_sets = row.planned_sets;
    } else {
      muscleGroupMap.set(row.muscle_group, {
        muscle_group: row.muscle_group,
        completed_sets: 0,
        planned_sets: row.planned_sets,
        remaining_sets: 0,
        mev: landmarks.mev,
        mav: landmarks.mav,
        mrv: landmarks.mrv,
        completion_percentage: 0,
        zone: 'below_mev',
        warning: null,
      });
    }
  });

  // Calculate derived fields and classify zones
  const muscleGroups: MuscleGroupVolumeTracking[] = [];
  muscleGroupMap.forEach((mg) => {
    mg.remaining_sets = Math.max(0, mg.planned_sets - mg.completed_sets);
    mg.completion_percentage =
      mg.planned_sets > 0 ? Math.round((mg.completed_sets / mg.planned_sets) * 100 * 10) / 10 : 0;

    mg.zone = classifyZoneWithOnTrack(mg.completed_sets, mg.planned_sets, mg.mev, mg.mav, mg.mrv);

    mg.warning = generateWarning(mg.zone, mg.muscle_group);

    muscleGroups.push(mg);
  });

  // Sort by muscle group name
  muscleGroups.sort((a, b) => a.muscle_group.localeCompare(b.muscle_group));

  return {
    week_start,
    week_end,
    muscle_groups: muscleGroups,
  };
}

/**
 * Get volume history over multiple weeks (T018)
 *
 * @param userId User ID
 * @param weeks Number of weeks to retrieve (default: 8, max: 52)
 * @param muscleGroupFilter Optional muscle group filter
 * @returns Historical volume data grouped by week
 */
export function getVolumeHistory(
  userId: number,
  weeks: number = 8,
  muscleGroupFilter?: string
): VolumeTrendsResponse {
  // Validate weeks parameter
  if (weeks < 1 || weeks > 52) {
    throw new Error('Weeks parameter must be between 1 and 52');
  }

  // Calculate start date (weeks ago from today)
  const endDate = new Date();
  const startDate = new Date();
  startDate.setDate(endDate.getDate() - weeks * 7);

  // Query volume by week
  const query = db.prepare(`
    SELECT
      w.date,
      mg.value as muscle_group,
      COUNT(s.id) as completed_sets
    FROM sets s
    JOIN workouts w ON s.workout_id = w.id
    JOIN exercises e ON s.exercise_id = e.id
    JOIN json_each(e.muscle_groups) mg
    WHERE w.user_id = ?
      AND w.status = 'completed'
      AND w.date >= ?
      AND w.date <= ?
      ${muscleGroupFilter ? 'AND mg.value = ?' : ''}
    GROUP BY w.date, mg.value
    ORDER BY w.date
  `);

  const params = [
    userId,
    startDate.toISOString().split('T')[0],
    endDate.toISOString().split('T')[0],
  ];
  if (muscleGroupFilter) {
    params.push(muscleGroupFilter);
  }

  const results = query.all(...params) as Array<{
    date: string;
    muscle_group: string;
    completed_sets: number;
  }>;

  // Group by week
  const weekMap = new Map<string, Map<string, number>>();

  results.forEach((row) => {
    const date = new Date(row.date);
    const { week_start } = getISOWeekBoundaries(date);

    if (!weekMap.has(week_start)) {
      weekMap.set(week_start, new Map());
    }

    const muscleGroupMap = weekMap.get(week_start)!;
    const currentSets = muscleGroupMap.get(row.muscle_group) || 0;
    muscleGroupMap.set(row.muscle_group, currentSets + row.completed_sets);
  });

  // Convert to response format
  const weekArray: Array<{
    week_start: string;
    muscle_groups: HistoricalVolume[];
  }> = [];

  weekMap.forEach((muscleGroupMap, week_start) => {
    const muscleGroups: HistoricalVolume[] = [];

    muscleGroupMap.forEach((completed_sets, muscle_group) => {
      const landmarks = VOLUME_LANDMARKS[muscle_group] || { mev: 0, mav: 0, mrv: 0 };
      muscleGroups.push({
        muscle_group,
        completed_sets,
        mev: landmarks.mev,
        mav: landmarks.mav,
        mrv: landmarks.mrv,
      });
    });

    // Sort muscle groups alphabetically
    muscleGroups.sort((a, b) => a.muscle_group.localeCompare(b.muscle_group));

    weekArray.push({
      week_start,
      muscle_groups: muscleGroups,
    });
  });

  // Sort weeks chronologically (oldest first)
  weekArray.sort((a, b) => a.week_start.localeCompare(b.week_start));

  return {
    weeks: weekArray,
  };
}

/**
 * Get program volume analysis (T019)
 *
 * Analyzes planned volume for the active program and classifies zones.
 *
 * @param userId User ID
 * @returns Program volume analysis or null if no active program
 */
export function getProgramVolumeAnalysis(userId: number): ProgramVolumeAnalysis | null {
  // Get active program
  const activeProgramQuery = db.prepare(`
    SELECT id, mesocycle_phase
    FROM programs
    WHERE user_id = ?
    ORDER BY created_at DESC
    LIMIT 1
  `);

  const activeProgram = activeProgramQuery.get(userId) as
    | { id: number; mesocycle_phase: 'mev' | 'mav' | 'mrv' | 'deload' }
    | undefined;

  if (!activeProgram) {
    return null;
  }

  // Query planned weekly sets by muscle group
  const plannedQuery = db.prepare(`
    SELECT
      mg.value as muscle_group,
      SUM(pe.sets) as planned_weekly_sets
    FROM program_exercises pe
    JOIN program_days pd ON pe.program_day_id = pd.id
    JOIN exercises e ON pe.exercise_id = e.id
    JOIN json_each(e.muscle_groups) mg
    WHERE pd.program_id = ?
    GROUP BY mg.value
  `);

  const results = plannedQuery.all(activeProgram.id) as Array<{
    muscle_group: string;
    planned_weekly_sets: number;
  }>;

  // Classify zones
  const muscleGroups = results.map((row) => {
    const landmarks = VOLUME_LANDMARKS[row.muscle_group] || { mev: 0, mav: 0, mrv: 0 };
    const zone = classifyZone(row.planned_weekly_sets, landmarks.mev, landmarks.mav, landmarks.mrv);

    return {
      muscle_group: row.muscle_group,
      planned_weekly_sets: row.planned_weekly_sets,
      mev: landmarks.mev,
      mav: landmarks.mav,
      mrv: landmarks.mrv,
      zone,
      warning: generateWarning(zone, row.muscle_group),
    };
  });

  // Sort by muscle group name
  muscleGroups.sort((a, b) => a.muscle_group.localeCompare(b.muscle_group));

  return {
    program_id: activeProgram.id,
    mesocycle_phase: activeProgram.mesocycle_phase,
    muscle_groups: muscleGroups,
  };
}

/**
 * Program Service
 *
 * Manages training program creation and configuration.
 * Provides default 6-day Renaissance Periodization split for new users.
 */

import { db } from '../database/db.js';
import type { Statement } from 'better-sqlite3';

/**
 * Program day configuration
 */
interface ProgramDay {
  day_of_week: number;
  day_name: string;
  day_type: 'strength' | 'vo2max';
}

/**
 * Exercise configuration for program
 */
interface ProgramExercise {
  program_day_id: number;
  exercise_id: number;
  order_index: number;
  sets: number;
  reps: string;
  rir: number;
}

/**
 * Stored program from database
 */
export interface Program {
  id: number;
  user_id: number;
  name: string;
  created_at: number;
}

/**
 * Program day from database
 */
export interface ProgramDayRecord {
  id: number;
  program_id: number;
  day_of_week: number;
  day_name: string;
  day_type: 'strength' | 'vo2max';
}

/**
 * Program exercise with exercise details
 */
export interface ProgramExerciseWithDetails {
  id: number;
  program_day_id: number;
  exercise_id: number;
  order_index: number;
  sets: number;
  reps: string;
  rir: number;
  exercise_name: string;
  muscle_groups: string;
  equipment: string;
}

/**
 * Default 6-day program structure
 */
const DEFAULT_PROGRAM_DAYS: ProgramDay[] = [
  { day_of_week: 1, day_name: 'Push A (Chest-Focused)', day_type: 'strength' },
  { day_of_week: 2, day_name: 'Pull A (Lat-Focused)', day_type: 'strength' },
  { day_of_week: 3, day_name: 'VO2max A (Norwegian 4x4)', day_type: 'vo2max' },
  { day_of_week: 4, day_name: 'Push B (Shoulder-Focused)', day_type: 'strength' },
  { day_of_week: 5, day_name: 'Pull B (Rhomboid/Trap-Focused)', day_type: 'strength' },
  { day_of_week: 6, day_name: 'VO2max B (30/30 or Zone 2)', day_type: 'vo2max' },
];

/**
 * Default exercise template for 6-day split
 * Exercise IDs are from the pre-seeded exercises table
 */
const getProgramExerciseTemplate = (programDayIds: number[]): ProgramExercise[] => {
  // Ensure we have all 6 program day IDs
  if (programDayIds.length !== 6) {
    throw new Error('Expected 6 program day IDs');
  }

  const day1 = programDayIds[0]!;
  const day2 = programDayIds[1]!;
  // day3 and day6 are VO2max days with no exercises, so we don't need them here
  const day4 = programDayIds[3]!;
  const day5 = programDayIds[4]!;

  return [
    // Day 1: Push A (Chest-Focused)
    { program_day_id: day1, exercise_id: 25, order_index: 1, sets: 3, reps: '6-8', rir: 3 }, // Barbell Back Squat
    { program_day_id: day1, exercise_id: 1, order_index: 2, sets: 4, reps: '6-8', rir: 3 }, // Barbell Bench Press
    { program_day_id: day1, exercise_id: 5, order_index: 3, sets: 3, reps: '8-10', rir: 2 }, // Incline Dumbbell Press
    { program_day_id: day1, exercise_id: 7, order_index: 4, sets: 3, reps: '12-15', rir: 1 }, // Cable Flyes
    { program_day_id: day1, exercise_id: 20, order_index: 5, sets: 4, reps: '12-15', rir: 1 }, // Lateral Raises
    { program_day_id: day1, exercise_id: 49, order_index: 6, sets: 3, reps: '15-20', rir: 0 }, // Tricep Pushdown

    // Day 2: Pull A (Lat-Focused)
    { program_day_id: day2, exercise_id: 68, order_index: 1, sets: 3, reps: '5-8', rir: 3 }, // Conventional Deadlift
    { program_day_id: day2, exercise_id: 14, order_index: 2, sets: 4, reps: '5-8', rir: 3 }, // Pull-Ups
    { program_day_id: day2, exercise_id: 10, order_index: 3, sets: 4, reps: '8-10', rir: 2 }, // Barbell Row
    { program_day_id: day2, exercise_id: 13, order_index: 4, sets: 3, reps: '12-15', rir: 1 }, // Seated Cable Row
    { program_day_id: day2, exercise_id: 16, order_index: 5, sets: 3, reps: '15-20', rir: 0 }, // Face Pulls
    { program_day_id: day2, exercise_id: 39, order_index: 6, sets: 3, reps: '8-12', rir: 1 }, // Barbell Curl

    // Day 3: VO2max A (Norwegian 4x4) - no exercises

    // Day 4: Push B (Shoulder-Focused)
    { program_day_id: day4, exercise_id: 27, order_index: 1, sets: 3, reps: '8-12', rir: 3 }, // Leg Press
    { program_day_id: day4, exercise_id: 18, order_index: 2, sets: 4, reps: '5-8', rir: 3 }, // Overhead Press
    { program_day_id: day4, exercise_id: 4, order_index: 3, sets: 3, reps: '8-12', rir: 2 }, // Dumbbell Bench Press
    { program_day_id: day4, exercise_id: 21, order_index: 4, sets: 4, reps: '15-20', rir: 0 }, // Cable Lateral Raises
    { program_day_id: day4, exercise_id: 22, order_index: 5, sets: 3, reps: '15-20', rir: 0 }, // Rear Delt Flyes
    { program_day_id: day4, exercise_id: 46, order_index: 6, sets: 3, reps: '8-10', rir: 2 }, // Close-Grip Bench Press

    // Day 5: Pull B (Rhomboid/Trap-Focused)
    { program_day_id: day5, exercise_id: 26, order_index: 1, sets: 3, reps: '6-8', rir: 3 }, // Front Squat
    { program_day_id: day5, exercise_id: 10, order_index: 2, sets: 4, reps: '6-8', rir: 3 }, // Barbell Row
    { program_day_id: day5, exercise_id: 12, order_index: 3, sets: 3, reps: '10-12', rir: 2 }, // Lat Pulldown (wide grip)
    { program_day_id: day5, exercise_id: 62, order_index: 4, sets: 4, reps: '12-15', rir: 1 }, // Barbell Shrugs
    { program_day_id: day5, exercise_id: 22, order_index: 5, sets: 3, reps: '15-20', rir: 0 }, // Rear Delt Flyes
    { program_day_id: day5, exercise_id: 41, order_index: 6, sets: 3, reps: '10-15', rir: 1 }, // Hammer Curl

    // Day 6: VO2max B (30/30 or Zone 2) - no exercises
  ];
};

/**
 * Create default 6-day Renaissance Periodization program for a new user
 *
 * @param userId - The user ID to create the program for
 * @returns The created program ID
 * @throws Error if program creation fails
 */
export function createDefaultProgram(userId: number): number {
  // Execute as a single transaction for atomicity
  const createProgram = db.transaction(() => {
    const now = Date.now();

    // 1. Create the program
    const stmtCreateProgram: Statement = db.prepare(`
      INSERT INTO programs (user_id, name, mesocycle_week, mesocycle_phase, created_at)
      VALUES (?, ?, ?, ?, ?)
    `);

    const programResult = stmtCreateProgram.run(
      userId,
      'Renaissance Periodization 6-Day Split',
      1,
      'mev',
      now
    );

    const programId = programResult.lastInsertRowid as number;

    // 2. Create the 6 program days
    const stmtCreateProgramDay: Statement = db.prepare(`
      INSERT INTO program_days (program_id, day_of_week, day_name, day_type)
      VALUES (?, ?, ?, ?)
    `);

    const programDayIds: number[] = [];

    for (const day of DEFAULT_PROGRAM_DAYS) {
      const dayResult = stmtCreateProgramDay.run(
        programId,
        day.day_of_week,
        day.day_name,
        day.day_type
      );
      programDayIds.push(dayResult.lastInsertRowid as number);
    }

    // 3. Create program exercises for all strength days
    const stmtCreateProgramExercise: Statement = db.prepare(`
      INSERT INTO program_exercises (program_day_id, exercise_id, order_index, sets, reps, rir)
      VALUES (?, ?, ?, ?, ?, ?)
    `);

    const exercises = getProgramExerciseTemplate(programDayIds);

    for (const exercise of exercises) {
      stmtCreateProgramExercise.run(
        exercise.program_day_id,
        exercise.exercise_id,
        exercise.order_index,
        exercise.sets,
        exercise.reps,
        exercise.rir
      );
    }

    // 4. Create today's workout for the user
    // Determine which day of week it is (0=Sunday, 1=Monday, ..., 6=Saturday)
    const dayOfWeek = new Date().getDay();

    // Map day of week to program_day_id
    // Sunday (0) -> Saturday's workout (index 5, VO2max B)
    // Monday (1) -> Push A (index 0)
    // etc.
    const dayMapping = [5, 0, 1, 2, 3, 4, 5]; // Sunday maps to Saturday's workout
    const todayProgramDayId = programDayIds[dayMapping[dayOfWeek]!]!;

    const stmtCreateWorkout: Statement = db.prepare(`
      INSERT INTO workouts (user_id, program_day_id, date, status, synced)
      VALUES (?, ?, date('now'), 'not_started', 0)
    `);

    stmtCreateWorkout.run(userId, todayProgramDayId);

    return programId;
  });

  return createProgram();
}

